css:
@-moz-document domain(dollars-bbs.org) {
  div.replytext blockquote {
    color: /*[[blockquote_color]]*/;
    font-weight: bold;
    margin: 0 auto !important;
    border-left: 1px solid /*[[blockquote_color]]*/;
    padding-left: 9px;
  }

  div.replytext blockquote:not(:last-child) {
    margin-bottom: -20px !important;
  }
}
