css:
/* i really want this to be global */
yt-img-shadow.ytd-channel-avatar-editor {
 border-radius: 0;
    
}

#avatar.ytd-c4-tabbed-header-renderer {
  width: 80px;height: 80px;margin: 0 24px 0 0;flex: none;border-radius: 0;background-color: transparent;overflow: hidden;
}

.yt-spec-button-shape-next--size-m {
 height: 32px;
 font-size: 12px;
 line-height: 32px;
 border-radius: 2.3px;
    
}

ytd-channel-video-player-renderer[rounded] #player.ytd-channel-video-player-renderer {
  overflow: hidden;border-radius: 2px;isolation: isolate;
}

ytd-thumbnail[size=medium] a.ytd-thumbnail,ytd-thumbnail[size=medium]:before {
  border-radius: 0;
    
}

ytd-thumbnail[size=large] a.ytd-thumbnail,ytd-thumbnail[size=large]:before {
  border-radius: 0px;
}


ytd-thumbnail[size=large] ytd-thumbnail-overlay-time-status-renderer.ytd-thumbnail,ytd-thumbnail[size=large] ytd-thumbnail-overlay-button-renderer.ytd-thumbnail {
  border-radius: 0px;
}

ytd-thumbnail[size] ytd-thumbnail-overlay-time-status-renderer.ytd-thumbnail,ytd-thumbnail[size] ytd-thumbnail-overlay-button-renderer.ytd-thumbnail {
  border-radius: 0px;
}

img.yt-img-shadow {
  display: block;margin-left: var(--yt-img-margin-left,auto);margin-right: var(--yt-img-margin-right,auto);max-height: var(--yt-img-max-height,none);max-width: var(--yt-img-max-width,100%);border-radius: 0;
}

ytd-searchbox[desktop-searchbar-style=rounded_corner_borders_light_btn] #container.ytd-searchbox {
  border-radius: 5px 0 0 5px;caret-color: var(--yt-spec-text-primary);margin-left: 32px;padding: 0px 4px 0px 16px;
}

ytd-searchbox[desktop-searchbar-style=rounded_corner_borders_light_btn] #search-icon-legacy.ytd-searchbox {
  border-radius: 0 5px 5px 0;
}

ytd-watch-metadata[modern-metapanel] #description.ytd-watch-metadata {
  border-radius: 3px;background: var(--yt-spec-badge-chip-background);
}

ytd-watch-metadata[skinny-mode] #description.ytd-watch-metadata {
  border-radius: 0;background: none;
}

html {
 --yt-spec-mono-filled-hover: #ffd044;
    
}

.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--filled {
  color: #6a562e;
  background-color: #fbd922;
}

ytd-post-renderer[rounded-container] {
  border-radius: 3px;
}

ytcp-icon-button.ytcp-quick-actions {
 background-color: var(--ytcp-brand-background-solid);
 border-radius: 50%;
}   


ytcp-chip {
 --chip-text-max-width: 290px;
 --chip-text-color: var(--ytcp-text-primary);
 --chip-text-margin-left: 12px;
 --chip-text-margin-right: 12px;
 display: inline-flex;
 height: 32px;
 border-radius: 16px;
 background: var(--ytcp-chip-background);
 cursor: pointer;
 align-items: center;
 justify-content: center;
 overflow: hidden;
 color: var(--chip-text-color);
 transition: background-color 50ms ease,box-shadow 100ms ease;
}

.image-thumbnail.ytcp-navigation-drawer {
 --icon-height: 24px;
 --icon-width: 24px;
 border-radius: 50%;
 background-color: transparent;
 overflow: hidden;
}
