css:
/* i really want this to be global */

.card-contents {
  background: #0D1117 !important
}

/* MAIN DARK COLOR */
textarea.textarea-input, .base-bar,
.card-contents .card-face-header,
.card-contents .header-row.main-row{
    background: #0D1117 !important;
    color:  rgb(201, 209, 217) !important;
}

/* main light dark color */

.smart-card-row, .deck-edit-section-detail, .deck-detail-header,
.smart-card, .first-row, .dashboard-pack-deck-list-header,
.deck-row-contents, .icon-button, .tab-item, .deck-list, 
.study-card-table, .modal-body,
.deck-card-list.text-rows,
.deck-card-list.text-rows,
.card-list-header .top-row,
.deck-edit-section-sidebar.is-full{
  background: rgb(45, 52, 61) !important;
}

/* light grey BGC */
.header-row, .card-face-header{
  background: #7C818C !important;
}

/* WHITE TEXTS */
.deck-name-caption, .field-label, .simple-text-button,
.add-prompt.create-prompt{
  color: white !important;
  opacity: 1;
}


.card-type-indicator, .deck-and-card-info * {
  color: #7C818C !important;
  opacity: 1 !important;
}

.pill-button {
  color: white !important;
  opacity: 1 !important;
  background: #0D1117 !important;
}


.tab-item,
.card-contents *,
.main-fields-container *,
.pack-name,
.value-data,
.new-deck-label,
.deck-pulldown,
.deck-name{
  color:  rgb(201, 209, 217) !important;
}


/* SHADOW */
.smart-card.edit-context {
  box-shadow: none !important
}

/* OVERLAY */
.loading-overlay {
    background: rgba(70, 70, 60, 0.9);
    color: white;
}

/* SIDE LIST */
.deck-edit-section-sidebar.is-full * {
  color: white !important
}

body *::-webkit-scrollbar {
  width: 0.25em;
  background-color: #333;
}
 
body *::-webkit-scrollbar-thumb {
  background-color: #fff;
}
 
body *::-webkit-scrollbar-thumb:hover {
  background-color: #aaa;
}
