css:
@-moz-document regexp('^https://bbs\\.ruliweb\\.com/.*/?board/.*$') {
  @media (max-width: 1159px) and (pointer: fine) {
    #nav {
      position: absolute;
      left: -10%;
      transition: left 0.3s ease-in-out;
      z-index: 1;
    }
    #nav:hover {
      left: 0;
    }

    #board {
      position: relative;
      left: -11%;
    }
  }
}
