css:
@-moz-document domain("hitomi.la")
{
body {
	background-color: rgb(30, 30, 30);
}

.container {
	background-color: rgb(30, 30, 30);
}

.content {
	background-color: rgb(30, 30, 30);
}

.top-content {
	background-color: rgb(30, 30, 30);
}

.bottom-content {
	background-color: rgb(30, 30, 30);
}

.page-content {
	background-color: rgb(30, 30, 30);
}

.page-content a {
  color:#984545;
}
a{
  color:#984545;
}

.page-container {
	background-color: rgb(30, 30, 30);
}

.gallery-content {
	background-color: rgb(30, 30, 30);
}

.gallery-content h1 {
	color: white;
}

#loader-content {
	background-color: rgb(30, 30, 30);
}

.dj, .cg, .acg, .manga, .anime {
	background-color: rgb(80, 80, 80);
}

.dj-content {
	background-color: rgb(50,50,50);
}

.dj-content td:first-child {
    color: white;
    width: 150px;
}


h1, center {
	color: whitesmoke;
}

select#single-page-select {
	color: rgb(153, 153, 153);
}

button#search-button {
	background-color: rgb(189, 189, 189);
}

body > div.navbar.navbar-inverse.navbar-static-top > div {
	background-image: none;
	background-color: rgb(30, 30, 30);
}

/*Manga*/

.manga {
	border: 1px solid rgba(255, 255, 255, 0.24);
  color:#9F399F;
}

.manga h1 {
	background-color: #743874;
	color: white;
	text-shadow: none;
}

.manga .artist-list {
	color: rgb(30, 30, 30);
	background-color: #AD81AD;
}

.manga-gallery h1 {
	background-color: #743874;
	color: rgb(30, 30, 30);
	text-shadow: none;
}

.manga-gallery h2 {
	color: rgb(30, 30, 30);
	background-color: #AD81AD;
}

/*Doujinshi*/

.dj {
	border: 1px solid rgba(255, 255, 255, 0.24);
  color:#B63636;
}

.dj h1 {
	background-color: #914C4C;
	text-shadow: none;
}

.dj .artist-list {
	color: rgb(30, 30, 30);
	background-color: #A66C6C;
}

.dj-gallery h1 {
	background-color: #BB7878;
	color: rgb(30, 30, 30);
	text-shadow: none;
}

.dj-gallery h2 {
	background-color: #663333;
	color: rgb(30, 30, 30);
}

/*Artist CG*/

.acg {
	border: 1px solid rgba(255, 255, 255, 0.24);
  color:#3EA5A5;
}

.acg h1 {
	background-color: #2A8C8C;
	text-shadow: none;
}

.acg .artist-list {
	color: rgb(30, 30, 30);
	background-color: #77BABA;
}

.thumbnail-list { 
	background-color: rgb(30, 30, 30);
}

.thumbnail-container img {
	border: 1px solid rgb(87, 87, 87);
}

.acg-gallery h1 {
	background-color: #2A8C8C;
	color: rgb(30, 30, 30);
	text-shadow: none;
}

.acg-gallery h2 {
	color: rgb(30, 30, 30);
	background-color: #77BABA;
}

/*Game CG*/

.cg {
	border: 1px solid rgba(255, 255, 255, 0.24);
  color:#4732D0;
}

.cg h1 {
	background-color: #5656CD;
	color: rgb(30, 30, 30);
	text-shadow: none;
}

.cg .artist-list {
	background-color: #8686DF;
	color: rgb(30, 30, 30);
}

.cg-gallery h1 {
	background-color: #5656CD;
	color: rgb(30, 30, 30);
	text-shadow: none;
}

.cg-gallery h2 {
	background-color: #8686DF;
	color: rgb(30, 30, 30);
}

/*Anime*/

.anime {
	border: 1px solid rgba(255, 255, 255, 0.24);
}

.anime h1 {
	background-color: #2F772F;
	color: rgb(30, 30, 30);
	text-shadow: none;
}

.anime .artist-list {
	background-color: #649E64;
	color: rgb(30, 30, 30);
}

.anime-gallery h1 {
	background-color: #2F772F;
	color: rgb(30, 30, 30);
	text-shadow: none;
}

.anime-gallery h2 {
	background-color: #649E64;
	color: rgb(30, 30, 30);
}

.video_backdrop {
	background-color: rgb(30, 30, 30);
}

/*Search Bar*/

#search-suggestions li {
	background-color: white;
}

#search-suggestions li a {
	background-color: white;
	color: #999999;
}
}
