css:
@-moz-document url-prefix(https://yugiohtopdecks.com/) {
  h1, h2, h3, h4, h5, h6 ,p, a, input, table td, .ui-autocomplete li {
  color: white !important;
}

body, .date-picker, .ui-widget-content, ul.inline-list.search-range a, tr:nth-child(odd) {
  background-color: #242526 !important;
  background: #242526 !important;
}

.panel, .announcement, input , .ui-datepicker td a, table tr:nth-child(even) {
  background-color: #313131 !important;
  background: #313131 !important;
}

ul.inline-list:not(.search-range) a, .button, .ui-datepicker-header, .ui-datepicker-calendar th, .ui-state-disabled, .ui-state-active, .ui-widget-content a:hover, ul.inline-list.search-range current, ul.inline-list.search-range a:hover, table th, .ui-state-focus, h5, .footer-disclaimer {
  background-color: #18191A !important;
  background: #18191A !important;
}

table, .ui-datepicker-prev, .ui-datepicker-next {
  background-color: white;
  border-spacing: 0;
}

table, .announcement, .ui-state-active, .ui-.widget-content .ui-state-active  {
  border: none !important;
}

table a:hover, .panel a:hover {
  color: #0089ff !important;
}

.ui-autocomplete li {
  font-weight: 400;
  padding: 0.25rem;
}

}
