css:
@-moz-document domain("emag.ro") {
.card-ads-listing::after {
    display: inline-block;
    width: 100%;
    height: 100%;
    background-color: rgba(6, 6, 6, 0.8);
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1;
    content:"/*[[text]]*/";
    font-size: 30px;
    text-align: center;
    transform: rotate(20deg) skew(10deg, -10deg) scale(0.9);
    padding-top: 100px;
    border-radius: 262px 54px / 39px 152px;
    border-top-left-radius: 262px 39px;
    border-top-right-radius: 54px 152px;
    border-bottom-right-radius: 262px 39px;
    border-bottom-left-radius: 54px 152px;
    font-family: impact;
    color: #ff5454;
    text-shadow: 0 0 16px #000;
    font-size: 63px;
}
}
