css:
@-moz-document domain(edgenuity.com) {
	.mainhead {
    background: linear-gradient(#000000, #000000, #000000);
}

#contentViewer {
    font-family: "Helvetica Neue", Arial, Helvetica, sans-serif;
    background-color: white;
    border-radius: 10px;
}
#tray {
    background-color: white;
    height: 50px;
    border-radius: 10px;
    opacity: 0.7;
}
.lesson-pane-trigger {
    background-color: #000000;
}
.overlay-background {
    display: block;
    position: fixed;
    z-index: 2000;
    opacity: 0.5;
    border-radius: 10px;
}
.icon-adv-open {
    color: #ff0707;
}
.mainfoot {
	opacity: 0.3;
}
.mainfoot a[disabled], .mainfoot a.disabled, .mainfoot a[disabled]:hover, .mainfoot a.disabled:hover {
    opacity: 0;
}

.overlay-time {
    color: #ffffff;
}

.overlay-attempt-header {
	background-color: rgba(0, 0, 0, 0);
    border-top: rgba(0, 0, 0, 0);
    border-right: rgba(0, 0, 0, 0);
    border-left: rgba(0, 0, 0, 0);
    height: 25px;
}
.overlay-attempt {
	background-color: rgba(0, 0, 0, 0);
}
#wrap  {
background-image: url('https://i.pinimg.com/originals/3e/4c/9d/3e4c9d81ba6be013081a1506dd7fcdec.gif');
    background-position: left top;
    background-size: 1920px 1080px;
    height: 1100px;
    background-repeat: no-repeat;
position: relative;
}
BODY {
background-color: #000000;
bottom: 5px;
overflow: hidden;
}
.Toolbar .FramesList {
top: 0px;
margin: 0px;
}
em#frameProgress {
color: #FFFFFF;
font-weight: bold;
text-decoration: underline;
}

HTML {
background-color: #000000;
}
#lesson-title {
    font-size: 24px;
    color: #000000;
    line-height: 26px;
    margin: 0px 0px 6px 0px;
font-weight: bold;
}
body#footer {
background-image: url('https://i.pinimg.com/originals/3e/4c/9d/3e4c9d81ba6be013081a1506dd7fcdec.gif');
}
.Toolbar .FramesList li.FrameComplete a {
border: 1px solid #000000;
}
.Toolbar .FramesList li.FrameCurrent a {
border: 1px solid #000000;
    background-image: linear-gradient(to bottom, #000000, #000000)
}
.Toolbar .FramesList li.FrameRight a {
    -webkit-border-radius: 160px;
}
.Toolbar .FramesList li a {
border: 1px solid #000000;
}
.Toolbar .FramesList li.FrameLeft a {
    -webkit-border-radius: 160px;
}
}
