css:
@-moz-document url-prefix("http://plataformagamificacion.cc.uah.es/pap")
{
body {
        color: #ffffff;
    /* background-color: black !important; */
    /* background: #151515; */
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    background: url(https://s9.gifyu.com/images/ezgif-4-3705e7f52e.gif) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    box-shadow: inset 0 0 0 1000px rgb(20 20 20 / 0.85);
}

.download-task-file {
    padding: 1.5rem;
    padding-left: 70px;
    color: white;
    display: inline-block;
    background-repeat: no-repeat;
    background-position: 30px center;
    cursor: pointer;
    transition: all 500ms;
    -moz-transition: all 500ms;
    -webkit-transition: all 500ms;
    -o-transition: all 500ms;
    margin-right: 10px;
    margin-bottom: 10px;
    font-weight: normal;
    outline: 0;
    border: 0;
    border-radius: 99rem;
    font-size: 100%;
    vertical-align: middle;
}

.hint {
    background-repeat: no-repeat;
    background-position: 10px center;
    padding: 2rem;
    padding-left: 60px;
    background-color: #00b1ff3d;
    background-image: url(https://images0.persgroep.net/rcs/LXCRpkzeu5YHEAwqmjp4y_3z6VI/diocontent/217686197/_fitwidth/763?appId=93a17a8fd81db0de025c8abd1cca1279&quality=0.8);
    margin-bottom: 10px;
    border-radius: 2rem;
    border: 1px solid #ffffff12;
    background-size: 3rem;
}
  
input[type="text"]:focus,input[type="password"]:focus {
  background:#ffffff14
}

.elgg-river-summary a, .elgg-anchor-label{
   transition: all 0.15s ease-in-out; /* para que el cambio sea animado */

}

.elgg-river-summary a:hover ,.elgg-anchor-label:hover  {
    color: white;
  /*font-weight:500;*/
  font-size:1.1rem;
}

.elgg-page-topbar a {
    -webkit-background-clip: text;
    font-variant-caps: petite-caps;
    /* font-weight: 900; */
    color: #ffffff;
    /* text-align: center; */
    letter-spacing: 0.5px;
    text-shadow: 0px 0px 5px #ffffff, 0px 0px 10px #b393d3, 0px 0px 10px #b393d3, 0px 0px 20px #b393d3;
    /* -webkit-text-stroke: 2px transparent; */
    /* background-image: linear-gradient(90deg,#000000,#000000); */
}

.elgg-menu-filter>.elgg-state-selected, .elgg-tabs>.elgg-state-selected {
    border-color: #151515;
    background: #151515;
}

.elgg-menu-filter>.elgg-state-selected>a, .elgg-tabs>.elgg-state-selected>a {
    position: relative;
    top: 0px;
    background: #fff;
}

.elgg-menu-page li:not(:last-child),.elgg-menu-owner-block li:not(:last-child) {
    border-bottom: 1px solid #393939;
}


/*Highlight comment*/
.elgg-comments .elgg-list>li.elgg-state-highlight, .elgg-river-comments>li.elgg-state-highlight {
   /* -webkit-animation: comment-highlight 5s;
    -moz-animation: comment-highlight 5s;
    animation: comment-highlight 5s; */
}
html {

    font-size: 16px;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    min-width: 300px;
    overflow-x: hidden;
    overflow-y: scroll;
    text-rendering: optimizeLegibility;
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    text-size-adjust: 100%;
}

/*fin highlight*/


/*Inicio de sesion*/
.elgg-button-submit {
    background: #ffffff;
    color: #000;
    border-radius: 99rem;
}
.elgg-page-walled-garden>.elgg-inner>div.elgg-page-body {
    padding: 1.5rem 3rem;
    background: #00000040;
    backdrop-filter: blur(10px);
    -webkit-box-shadow: 1px 1px 1px rgb(0 0 0 / 30%), -1px -1px 1px rgb(0 0 0 / 30%);
    box-shadow: 1px 1px 1px rgb(0 0 0 / 30%), -1px -1px 1px rgb(0 0 0 / 30%);
    border-radius: 2rem;
    border: 1px solid;
    border-color: #ffffff2e;
}
.elgg-menu-login>li>a {
    padding: .5rem 1rem;
    font-size: .9rem;
    color: #000000;
    display: block;
    background: white;
    border-radius: 2rem;
    text-align: center;
    width: 20rem;
    margin: auto;
    flex-wrap: auto;
    margin-top: 0.75rem;
}

.elgg-page-walled-garden-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url(https://r4.wallpaperflare.com/wallpaper/531/951/621/digital-digital-art-artwork-illustration-minimalism-hd-wallpaper-08869d3810a06c28a09cf1be487204ea.jpg) !important;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: 50%;
    /* -webkit-filter: blur(4px); */
    filter: blur(0px);
}

  
.elgg-page-walled-garden>.elgg-inner>div.elgg-page-header {
    padding: 3rem;
    text-align: center;
}
  
.elgg-page-footer {
    position: relative;
    background: #fafafa;
    border-top: 0px solid #e6e6ea !important;
    color: #969696;
    font-size: .9rem;
    min-height: 4rem;
}

/*fin Inicio sesion*/

  /*notificaciones custom*/

.custom-river.bg06 {
    background-image: url(data:image/jpeg;base64,);
}
  /*fin notificaciones custom*/




/*Animación de la barra superior*/
.elgg-page-topbar .elgg-menu li a {
  transition: transform 0.1s ease-in-out, text-shadow 0.1s ease-in-out;
  -webkit-background-clip: text;
  font-variant-caps: petite-caps;
  color: #ffffff;
  letter-spacing: 0.5px;
  text-shadow: 0px 0px 5px #ffffff;
}

.elgg-page-topbar .elgg-menu li:hover a {
  transform: scale(1.15);
  text-shadow: 0px 0px 5px #ffffff, 0px 0px 10px #b393d3, 0px 0px 10px #b393d3, 0px 0px 20px #b393d3;
}

.elgg-page-topbar .elgg-menu li.elgg-state-selected a {
  transform: scale(1.5);
  text-shadow: 0px 0px 10px #ffffff, 0px 0px 15px #b393d3, 0px 0px 15px #b393d3, 0px 0px 30px #b393d3;
}
/*fin animacion barra superior*/


.elgg-list>li {
  border-bottom: none;
  padding: 1rem;
  margin: .75rem;
  background: #ffffff14;
  border-radius: 1.5rem;
  transition: all 0.15s ease-in-out; /* para que el cambio sea animado */
}

.elgg-list>li:hover {
  transform: scale(1.05); /* aumenta el tamaño del elemento */
}


.elgg-menu-filter>li>a:hover, .elgg-tabs>li>a:hover {
    background-color: #29505c;
    color: #ffffff;
    border-radius: inherit;
}

/*Quitar borde raro comentarios*/
.elgg-comments .elgg-list>li, .elgg-river-comments>li {
    /* border-color: #0000f2; */
    /* border-style: solid; */
    border-width: 0 0px 1px 1px;
    padding: 1rem;
}
/*fin quitar borde raro comentarios*/


select:not([multiple]) {
    height: 2.5rem;
    border-radius: 99rem;
}

.elgg-owner-block>.elgg-head, .elgg-page-menu>.elgg-body {
    /* padding: 1.5rem; */
    border-radius: 99rem;
    background: #ffffff14;
    text-align: center;
    align-items: center;
    max-width: 40rem;
    align-self: center;
    margin: auto;
    margin-top: 1rem;
}

.elgg-image-block.elgg-chip {
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    margin: auto;
}


.elgg-page-topbar .elgg-menu li .elgg-child-menu {
    display: none;
    -webkit-flex-direction: column;
    -ms-flex-direction: column;
    flex-direction: column;
    position: absolute;
    top: 4rem;
    right: 0;
    width: 12rem;
    background-image: linear-gradient(to bottom left, #553c9a, #ee4b2b);
    -webkit-align-items: flex-start;
    -ms-flex-align: start;
    align-items: flex-start;
    -webkit-box-shadow: 1px 2px 4px rgb(0 0 0 / 20%);
    box-shadow: 1px 2px 4px rgb(0 0 0 / 20%);
}

