.elgg-menu-filter, .elgg-tabs {
  margin-bottom: .5rem;
  border-bottom: 1px solid #e6e6ea;
  display: -ms-flexbox;
  display: -webkit-flex;
  display: flex;
  -webkit-flex-direction: row;
  -ms-flex-direction: row;
  flex-direction: row;
  -webkit-flex-wrap: wrap;
  -ms-flex-wrap: wrap;
  flex-wrap: wrap;
}

.elgg-menu-filter li {
  transition: 0.1s ease-in-out;
}

.elgg-menu-filter li:hover {
  transform: scale(1.05);
}


.elgg-page-topbar {
    /* background:#0078ac; */
    background-image: linear-gradient(to bottom left, #553c9a, #ee4b2b);
}

a {
    color: #fbabc3;
}


.elgg-list-river {
    border-bottom: 1px solid #363636;
}
.elgg-menu-filter>li, .elgg-tabs>li {
    background: #fafafa; /*cambiar por fafafa*/
    -webkit-flex-grow: 1;
    flex-grow: 1;
    border-radius: 40rem;
    margin-left: 1rem;
    margin-right: 1rem;
}

.elgg-menu-filter, .elgg-tabs {
    margin-bottom: .5rem;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: row;
    -ms-flex-direction: row;
    flex-direction: row;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
}

.elgg-menu-filter>.elgg-state-selected>a, .elgg-tabs>.elgg-state-selected>a {
    position: relative;
    border-radius: 40rem;
    border-top-left-radius: 40rem;
    border-top-right-radius: 40rem;
    border-bottom-right-radius: 40rem;
    border-bottom-left-radius: 40rem;
    color: white;
    font-weight: 700;
    background-image: linear-gradient(to bottom left, #553c9a, #ee4b2b);
}

.elgg-layout-header>h1, .elgg-layout-header>h2, .elgg-layout-header>h3 {
    -webkit-order: 1;
    -ms-flex-order: 1;
    order: 1;
    -webkit-flex-basis: 30%;
    flex-basis: 30%;
    -webkit-flex-grow: 1;
    flex-grow: 1;
    padding: 1rem;
    text-align: center;
    font-size: 2rem;
    font-weight: 200;
}

.leaderboard .user {
    background-color: #ffffff0d;
    color: white;
    border-radius: 1rem;
}

.leaderboard .user a, .leaderboard .user .task_points {
    color: #f7adc3;
}

.store_item {
    padding: 10px;
    padding-right: 15px;
    width: inherit;
    margin: auto;
    clear: both;
    background-color: #ffffff1c;
    margin-top: 15px;
    overflow: hidden;
    -webkit-box-sizing: initial;
    -moz-box-sizing: initial;
    box-sizing: initial;
    border-radius: 1rem;
}

#profile-details {
    /* border: 1px solid #e6e6ea; */
    background: #ffffff1a;
    margin-bottom: 1rem;
    border-radius: 1rem;
    padding: 2rem;
}

.elgg-menu-page, .elgg-menu-owner-block {
    background: none;
    border: 0px solid #e6e6ea;
    margin-bottom: 2rem;
}

.elgg-menu-page li:not(:last-child), .elgg-menu-owner-block li:not(:last-child) {
    border-bottom: none;
}


/*quitar*/
.elgg-page-topbar .elgg-menu li>a:hover,.elgg-page-topbar .elgg-menu li.elgg-state-selected>a {
    background-color: rgb(131 44 48)
}

.elgg-menu-page li>a, .elgg-menu-owner-block li>a {
    background-image: linear-gradient(to bottom, #8fe5ff, #57a5e1);
    padding: .5rem 1rem;
    color: #ffffff;
    margin: 1rem;
    text-align: center;
    /* max-width: 20%; */
    border-radius: 999rem;
}

.task a {
    /* color: white; */
    padding: 10px 2px;
    display: block;
    width: 625px;
    padding-right: 103px;
    transition: all 500ms;
    -moz-transition: all 500ms;
    -webkit-transition: all 500ms;
    -o-transition: all 500ms;
}


#start-page a.left {
    background-position: 1% center;
    padding-left: 130px;
    border-radius: 2rem;
}

/*cambiar fotos de perfil por la de rico*/
.elgg-avatar>a img {
    display: inline-block;
    vertical-align: middle;
    width: 100%;
    height: auto;
    /*content: url(https://www.elconfidencialdigital.com/media/elconfidencialdigital/images/2022/12/19/2022121912431623323.jpg);*/
    text-size-adjust: auto;
}

#start-page a.right {
    background-position: 99% center;
    padding-right: 130px;
    text-align: right;
    border-radius: 2rem;
}

a#start-tasks {
    background-color: #0062ac;
    background-image: url(https://www.elconfidencialdigital.com/media/elconfidencialdigital/images/2022/12/19/2022121912431623323.jpg);
    border-radius: 3px;
    background-size: 7rem;
}


/*Text area comentar*/
input, textarea {
    border: 1px solid #dcdcdc;
    color: #ffffff;
    font-size: 1rem;
    padding: .25rem .5rem;
    line-height: normal;
    width: 100%;
    border-radius: 1rem;
    background: #ffffff14;
}

/*Logros chidos*/
.achievement {
    padding: 15px;
    color: #333;
    background-color: #ccc;
    margin-bottom: 6px;
    border-radius: 1rem;
}
/*Fin logros chidos*/

/*TextArea de The Wire*/
#thewire-textarea {
    padding: 6px;
    background: #ffffff14;
}

/*Base text areas*/
input, textarea {
    border: 1px solid #dcdcdc;
    color: #ffffff;
    font-size: 1rem;
    padding: .25rem .5rem;
    line-height: normal;
    width: 100%;
    border-radius: 1rem;
    background: #ffffff14;
}

/*Corregir tareas*/
.post-correction-form {
    margin: auto;
    margin-top: 10px;
    padding: 15px;
    background-color: #151515;
    display: block;
}
.criteria-entry {
    display: inline-block;
    width: 60%;
    padding: 10px;
    background-color: #ffffff14;
    border-radius: 99rem;
    padding: 0.75rem;
}
.task-solved {
    padding: 1rem;
    padding-left: 50px;
    background-image: url(https://creazilla-store.fra1.digitaloceanspaces.com/icons/3506993/green-checkmark-circle-icon-md.png);
    background-size: 30px;
    width: auto;
    margin-top: 7px;
    background-color: #ffffff14;
    border-radius: 1rem;
}
/*Fin de corregir tareas*/


/*Notificaciones en azul - info*/
.elgg-message.elgg-message-help, .elgg-message.elgg-message-info, .elgg-message.elgg-message-notice {
    color: #3b8bc9;
    background-color: #e7f1f9;
    border-color: #b1d1e9;
    border-radius: 2rem;
}
.elgg-message>.elgg-inner>.elgg-head {
    /* background-color: rgba(255,255,255,.7); */
    font-weight: bold;
    padding: .5rem 1rem;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: inherit;
}
/*Fin notif*/


/*editor de texto*/
.cke.cke_chrome {
    -webkit-box-shadow: none;
    box-shadow: none;
    border-color: #151515;
    border-radius: 1rem;
    overflow: hidden;
}

.cke .cke_top, .cke .cke_bottom {
    background: #151515;
    /* border-color: #151515; */
}

.cke_editable {
    cursor: text;
    color: white;
}

.cke_inner {
    display: block;
    /* background: #fff; */
    padding: 0;
    -webkit-touch-callout: none;
}

