css:
@-moz-document domain(thunkable.com) {
    /*darkmode*/
    * { color: #aaa !important; background-color: #000 !important; }

    /*darkmode blocks background*/
    #blocklyDiv > div.blocklyDiv > div > div > div > svg.blocklySvg > g > rect.blocklyMainBackground {fill: #0f0f0f !important;}

    /*elements move*/
    #blocklyDiv > div.blocklyDiv > div > div > div > svg.blocklyBlockDragSurface {background-color: transparent !important;}

    /*background move*/
    #blocklyDiv > div.blocklyDiv > div > div > div > svg.blocklyWsDragSurface {background-color: transparent !important;}

    /*tools submenu*/
    #blocklyDiv > div.blocklyDiv > div > div > div > svg.blocklyFlyout > path {fill: transparent !important;}
}

