css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iszene.com") {

body {background: #F7F7F7!important; }
table {	color: #000000!important;font-family: Arial, Helvetica, sans-serif!important;}

.container_top, .container_bottom, .container_body {background: none!important;}

.ad {background: url(http://i41.tinypic.com/ngoniv.jpg) no-repeat top center!important;}

.content_top {background:url(http://i41.tinypic.com/nzq6b9.jpg) no-repeat scroll left top transparent!important; margin-top:35px!important; }
.content_left {background: url(http://i39.tinypic.com/k9sy1z.jpg) repeat-y top right!important; width: 19px!important;}
.content_right {background: url(http://i42.tinypic.com/2z5iwj7.png) repeat-y top left!important;width: 18px!important;}
.content_bottom {background: url(http://i40.tinypic.com/zwnv52.jpg) no-repeat top center!important;}
.content_top table  {padding:0px!important;font-size:12px!important;margin-top:-2px;}
.content {background: #ffffff !important;}

.thead {background: url(http://i39.tinypic.com/2pshqux.jpg) top left no-repeat!important;width:875px!important;}
.thead div.smalltext {font-size: 12px!important; margin-top:8px;}
.thead strong {margin-left:4px!important;}
.tfoot {background: url(http://i42.tinypic.com/i5q71g.png) bottom center no-repeat!important;
}


.header_top {background:none!important;width: 0px!important;height: 0px!important;padding: 0!important;}
.logo {display:none!important;padding: 0!important;}

.search_block {background: url(http://i42.tinypic.com/108emus.png) no-repeat top left;float: none!important;position:absolute!important;margin-left:822px!important;margin-top:-40px!important;z-index:100;}
.search {background: none no-repeat top left!important;height: 36px!important;padding: 0!important;width: 160px!important;}
.search input.textbox {font-size: 12px!important;margin-left: 26px!important;margin-top: 10px!important;width:110px;}
.search_block tr:last-child  {display:none;}
.search_block table tr td:last-child {display:none;}

.menu {background: url(http://i41.tinypic.com/sd0nbr.png) no-repeat center!important;height: 36px!important;width: 982px!important;margin: auto auto;position:absolute;top:18px;}
.menu table tr {position:absolute!important; margin-left:-26px; margin-top:1px;}
.menu_trow {background: transparent;height: 36px!important;padding: 0px 0px!important;}
.menu_trow:hover {background: none repeat-x bottom center!important;height: 36px!important;padding: 0px 0px!important;}
/* .menu table tr td:first-child { background: #000; width:1%;} */
/* .menu table tr td:last-child { background: #000; height: 36px!important;} */
.forum {background: url(http://i40.tinypic.com/3178nj7.png) no-repeat top center!important;height: 36px!important;width: 116px!important;padding-right:1px!important;}
.portal {background: url(http://i39.tinypic.com/bgzm74.png) no-repeat top center!important;height: 36px!important;width: 116px!important;padding-right:1px!important;}
.podcast {background: url(http://i42.tinypic.com/2m4vh2p.png) no-repeat top center!important;height: 36px!important;width: 116px!important;padding-right:1px!important;}
.reviews {background: url(http://i40.tinypic.com/2nb4sjc.png) no-repeat top center!important;height: 36px!important;width: 116px!important;padding-right:1px!important;}
.anleitungen {background: url(http://i41.tinypic.com/bhmucx.png) no-repeat top center!important;height: 36px!important;width: 116px!important;padding-right:1px!important;}
.downloads {background: url(http://i41.tinypic.com/mrxqqb.png) no-repeat top center!important;height: 36px!important;width: 116px!important;padding-right:1px!important;}
.wiki {background: url(http://i39.tinypic.com/ngukp0.png) no-repeat top center!important;	height: 36px!important;	width: 116px!important;	padding-right:1px!important;}

.forum:hover {background: url(http://i41.tinypic.com/wkl4zd.png) no-repeat top center!important;height: 36px!important;}
.portal:hover {background: url(http://i40.tinypic.com/15fm42e.png) no-repeat top center!important;height: 36px!important;}
.podcast:hover {background: url(http://i39.tinypic.com/10yphmd.png) no-repeat top center!important;height: 36px!important;}
.reviews:hover {background: url(http://i43.tinypic.com/hwl6kp.png) no-repeat top center!important;height: 36px!important;}
.anleitungen:hover {background: url(http://i39.tinypic.com/34hcq4x.png) no-repeat top center!important;}
.downloads:hover {background: url(http://i44.tinypic.com/io1c15.png) no-repeat top center!important;}
.wiki:hover {background: url(http://i44.tinypic.com/29zrn68.png) no-repeat top center!important;}

.text_bottom_body {background: url(http://i39.tinypic.com/bhhj7q.png) repeat-y center!important;}
.text_bottom_top {background: url(http://i42.tinypic.com/2z6s70n.png) no-repeat bottom center!important;}
.text_bottom_bottom {background: url(http://i44.tinypic.com/api34x.png) no-repeat top center!important;}

.footer {background: url(http://i44.tinypic.com/api34x.jpg) no-repeat top center!important;}

.portal_tab a:active, .tab_active, .portal_tab a:link, .portal_tab a:visited, .portal_tab a:hover, .text_bottom_body, .footer, .navigation, .tfoot, .thead, .welcome_header, .welcome_text, .footer_copyrigh {text-shadow: none!important;}


}
