/* i really want this to be global */




/*TIX On Topbar*/





#navbar-robux {
    position: relative;
    top: -18px;
}

.rbx-header .rbx-navbar-icon-group>li, .rbx-header .rbx-navbar-right-nav>li {
    float: left;
    padding: 2px;
}
.rbx-header .rbx-navbar-icon-group li, .rbx-header .rbx-navbar-right-nav li {
    text-align: center;
}

.nav>li, .nav>li>a {
    position: relative;
    display: block;
}
.nav>li, .nav>li>a {
    position: relative;
    display: block;
}
li, ul {
    list-style: none;
    margin: 0;
    padding: 0;
}
*, :after, :before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
*, :after, :before {
    -webkit-box-sizing: inherit;
    -moz-box-sizing: inherit;
    box-sizing: inherit;
}
user agent stylesheet
li {
    display: list-item;
    text-align: -webkit-match-parent;
}
.rbx-header .rbx-navbar-icon-group {
    margin: 0;
    line-height: 28px;
    float: right;
}
.nav {
    margin-bottom: 0;
    padding-left: 0;
    list-style: none;
}







.light-theme .btn-navigation-nav-robux-md {
    background-color: transparent;
    border-color: rgba(0,0,0,.6);
    color: rgba(0,0,0,.6);
    border-radius: 8px;
}

.btn-navigation-nav-robux-md, a.btn-navigation-nav-robux-md, button.btn-navigation-nav-robux-md {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: #fff;
    color: #191919;
    cursor: pointer;
    display: inline-block;
    font-weight: 500;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 4px;
    font-size: 16px;
    line-height: 100%;
    border-radius: 3px;
    border: none;
}

#navbar-robux:after {
    content: url(data:image/png;base64,);
    top: 16px;
    position: relative;
    cursor: not-allowed;
    content: url(data:image/png;base64,);
}
*, :after, :before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
*, :after, :before {
    -webkit-box-sizing: inherit;
    -moz-box-sizing: inherit;
    box-sizing: inherit;
}
