css:
@-moz-document url-prefix("https://www.jeuxvideo.com") {
div[class="header__nav header__nav--primary"] {margin-left:170px;}

@media screen and (max-width:1200px) {div[class="header__nav header__nav--primary"] {margin-left:0px;} }

a[class="xXx header__logo"] {background-repeat:no-repeat;
background-position:center;
display:block;
padding-left:220px;}

a[class="xXx header__logo"]{background-image: url("https://upload.wikimedia.org/wikipedia/commons/thumb/3/3b/Jeuxvideocomlogo.png/800px-Jeuxvideocomlogo.png");
background-size: 220px auto;}

.theme-dark a[class="xXx header__logo"]{background-image: url("https://www.totalbug.com/wp-content/uploads/2018/05/jeuxvideo-com.png");
background-size: 230px auto;}
}
