css:
@-moz-document domain(www.roblox.com) {
*{font-family: "Comic Sans MS", "Comic Sans", cursive;}
body{background-color:yellow;}
h1{color:blue;}
h2{color:red;}
h3{color:yellow;}
h4{color:green;}
h5{color:purple;}
h6{color:orange;}
p{color:purple;font-size:20px;}
pre{font-size:30px}
div{
  background-color: #FF0000;
  animation: bgColor 2s infinite linear;
}
@keyframes bgColor {
  12.5% {
    background-color: #FF0000;
  }
  25% {
    background-color: #FFA500;
  }
  37.5% {
    background-color: #FFFF00;
  }
  50% {
    background-color: #7FFF00;
  }
  62.5% {
    background-color: #00FFFF;
  }
  75% {
    background-color: #0000FF;
  }
  87.5% {
    background-color: #9932CC;
  }
  100% {
    background-color: #FF1493;
  }
}
a{color:yellow; background-color:#bb00ff;font-family: "Comic Sans MS", "Comic Sans", cursive;font-size:20px;}
span{color:yellow;font-family: "Comic Sans MS", "Comic Sans", cursive;font-size:20px;}
input.form-control.input-field.new-input-field{font-family: "Comic Sans MS", "Comic Sans", cursive;font-size:20px;}
ul{background-color:yellow;}
li{background-color:#95ff00;}
span.font-header-2.dynamic-ellipsis-item{background-color:orange;font-size:16px;color:yellow;}
span.icon-nav-blog{background-color:gray;}
span.icon-nav-shop{background-color:#ff00c3;}
span.notification-blue.notification{font-size:30px; font-family:"Comic Sans MS", "Comic Sans", cursive; background-color:lime;}
span.icon-nav-giftcards{background-color:black;}
div.game-card-name.game-name-title{font-size:17px;font-family: "Comic Sans MS", "Comic Sans", cursive;}
span.xsmall.notification-red.notification.ng-binding{font-family: "Comic Sans MS", "Comic Sans", cursive;font-size:20px; background-color:lime;}
span.message-piece.font-caption-body.text-emphasis.ng-binding.ng-scope{font-size:20px;background-color:#0073ff;color:pink;}
span.font-footer.text-muted.indicated-message-bubble.ng-binding{font-size:30px;color:yellow;}
label{background-color:blue;color:yellow;}
div.content{background-color:yellow;}
main.container-main{background-color:blue;}
button{color:red;background-color:lime;font-family: "Comic Sans MS", "Comic Sans", cursive;font-size:20px;}
div.item-card-name.ng-binding{background-color:#ac8ccf;color:#34522a;}
div.profile-accoutrements-container{background-color:yellow;}
div.slide-item-emblem-container{background-color:purple;}
#nav-robux-icon{background-color:green;}
a.text-name.text-overflow{font-size:25px;}
span.small.text-title.text-overflow.font-caption-header.chat-friend-name.dynamic-ellipsis-item.ng-binding.read{color:red;background-color:blue;}
p.text-footer.footer-note{font-size:30px}
a.see-all-button{font-size:25px}
a.text-link{background-color:red;font-size:100px}
img{
  width: 300px;
  animation: rotation 1s infinite linear;
}

@keyframes rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
}
