css:
@-moz-document domain(www.youtube.com) {
*{font-family: "Comic Sans MS", "Comic Sans", cursive;}
img{
  animation: rotation 1s infinite linear;
}

@keyframes rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
div#contents, #guide-inner-content, iron-selector, ytd-watch-flexy{
  background-color: #FF0000;
  animation: bgColor 2s infinite linear;
}
@keyframes bgColor {
  12.5% {
    background-color: #FF0000;
  }
  25% {
    background-color: #FFA500;
  }
  37.5% {
    background-color: #FFFF00;
  }
  50% {
    background-color: #7FFF00;
  }
  62.5% {
    background-color: #00FFFF;
  }
  75% {
    background-color: #0000FF;
  }
  87.5% {
    background-color: #9932CC;
  }
  100% {
    background-color: #FF1493;
  }
}
h1{font-family: "Comic Sans MS", "Comic Sans", cursive;}
h2{font-family: "Comic Sans MS", "Comic Sans", cursive;}
h3{font-family: "Comic Sans MS", "Comic Sans", cursive;}
h4{font-family: "Comic Sans MS", "Comic Sans", cursive;}
h5{font-family: "Comic Sans MS", "Comic Sans", cursive;}
h6{font-family: "Comic Sans MS", "Comic Sans", cursive;}
yt-formatted-string{color:yellow;background-color:red;animation:bgColor 3s infinite linear;}
a{font-size:30px;background-color:blue;}
div#container{background-color:lime;}
div#guide-links-secondary, #guide-links-primary, #items{background-color:yellow;}
div#actions{background-color:blue;}
div#owner{background-color:lime;}
div#top-row{background-color:yellow;}
div#primary{background-color:orange;}
div#player{animation:bgColor 10s infinite linear;}
span{font-size:130%;}
div#header{background-color:lime;}
button{animation:rotation 3s infinite linear}
a.ytp-button{animation:rotation 3s infinite linear;}
div#channel-header{animation:bgColor 1s infinite linear}
}
