css:
@-moz-document domain('dev.to') {
  /* header.crayons-header, */

  aside.crayons-layout__sidebar-left,
  aside.crayons-layout__sidebar-right,
  section#comments,
  div.crayons-card.crayons-card--secondary.crayons-sponsorship-article,
  div .crayons-card.crayons-card--secondary.text-padding.mb-4.print-hidden,
  footer.crayons-footer {
      display: none !important;
  }

  div.crayons-layout.crayons-layout--3-cols.crayons-layout--article {
        grid-template-columns:  var(--layout-content-width);
  }

  div.crayons-layout {
      max-width: 100% !important;
  }
}
