css:
@-moz-document domain(www.reddit.com) {
*{font-family: "Comic Sans MS", "Comic Sans", cursive;}
a{font-family: "Comic Sans MS", "Comic Sans", cursive;}
span{font-family: "Comic Sans MS", "Comic Sans", cursive;}
div{font-family: "Comic Sans MS", "Comic Sans", cursive;}
div._1zPvgKHteTOub9dKkvrOl4{font-family: "Comic Sans MS", "Comic Sans", cursive;}
div._31N0dvxfpsO6Ur5AKx4O5d{
  background-color: #FF0000;
  animation: bgColor 2s infinite linear;
}
@keyframes bgColor {
  12.5% {
    background-color: #FF0000;
  }
  25% {
    background-color: #FFA500;
  }
  37.5% {
    background-color: #FFFF00;
  }
  50% {
    background-color: #7FFF00;
  }
  62.5% {
    background-color: #00FFFF;
  }
  75% {
    background-color: #0000FF;
  }
  87.5% {
    background-color: #9932CC;
  }
  100% {
    background-color: #FF1493;
  }
}
img.Mh_Wl6YioFfBc9O1SQ4Jp.u0UgpXN5r-VO6PP9OAViq, ._2KqgQ5WzoQRJqjjoznu22o, .tk5Gqiw2uQ1xL-VWicKBG {
  animation: rotation 0.3s infinite linear;
}

@keyframes rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
button, i, svg{animation:rotation 1s infinite linear}
h1{background-color:blue;font-family: "Comic Sans MS", "Comic Sans", cursive;}
h2{background-color:yellow;font-family: "Comic Sans MS", "Comic Sans", cursive;}
h3{color:yellow;background-color:red;font-family: "Comic Sans MS", "Comic Sans", cursive;}
h4{color:lime;background-color:yellow;font-family: "Comic Sans MS", "Comic Sans", cursive;}
h5{background-color:orange;font-family: "Comic Sans MS", "Comic Sans", cursive;}
h6{background-color:purple;font-family: "Comic Sans MS", "Comic Sans", cursive;}
i{animation:bgColor 1s infinite linear}
div._3JDs8KEQIXSMn1bTF2ZqJ_{background-color:lime;}
div.MSTY2ZpsdupobywLEfx9u{background-color:red;}
img._34CfAAowTqdbNDYXz5tBTW{animation:rotation 1s infinite linear}
p{animation:bgColor 1s infinite linear;font-family: "Comic Sans MS", "Comic Sans", cursive;}
div.uI_hDmU5GSiudtABRz_37, ._1poyrkZ7g36PawDueRza-J, ._3JgI-GOrkmyIeDeyzXdyUD, ._1oQyIsiPHYt6nx7VOmd1sz, ._2vkeRJojnV7cb9pMlPHy7d, ._1npCwF50X2J7Wt82SZi6J0, .wBtTDilkW_rtT2k5x3eie{animation:bgColor 1s infinite linear}
input{animation:bgColor 3s infinite linear;}
}
