css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myyearbook.com/apps/match"), url("http://www.myyearbook.com/apps/match") {



html {
background-color: #000000 !important
}

#content {
border-color: red !important;
background-color: #000000 !important;
}
.MatchGame {
color: #FFFFFF !important;
}
#matchCurrent a, #matchCurrent a:visited {
color: #ffcc33 !important;
}
a[class='thingsInCommon']
{color: #ffcc33 !important;}

a[class='improve']
{color: #ffcc33 !important;}

#matchFlow div.matchFlowCurrent {
background-color: #000000 !important;
border-color: #ffcc33 !important;
}
#matchFlow .BlindDate-compatibility {
background-color: #000000 !important; 
}
div[id='flowGoPrev']
{background-color: #000000 !important;}

div[id='flowGoPrev']
{border-color: #FF0000 #FF0000 !important;}

div[id='flowGoPrev']
{border-style: inset inset none !important;}

div[class='matchFlowBox matchFlowPrevious']
{border: 1px solid #000 !important;}

div.matchFlowNext {
border: 0px solid #000000 !important;
}

td[class='tabItem']
{background: none !important;}

table.matchTab td.selected {
background: none !important;
border-bottom-color: #000000 !important;
}


table.tabMenu td.tabItem {
background: none !important;
}

#matchFilters {
color: #000000 !important;
background-color: #000000 !important;
}

}
