css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myyearbook.com/apps/match/board"), url("http://www.myyearbook.com/apps/match/board") {


html {
background-color: #000 !important;
}

#content {
background-color: #000 !important;
border-color: #FF0000 !important;
}

table.tabMenu td.tabItem {
background: none !important;
}
table.matchTab td.selected {
border-bottom-color: #FF0000 !important;
}
#matchContent {
background-color: #000000 !important;
}
.matchBoard td.matchImg {
border: none !important;
}
.MatchBoard {
color: #ffcc33 !important;
}
#guessesLeft {
color: #ffcc33 !important;
}
div.miniProfile {
background-color: #000 !important;
border: 1px solid #FF0000 !important;
}
#matchContent a, #matchContent a:visited {
color: #FFF !important;
}
div.miniProfileActionIcons, div.miniProfileAboutMe {
background-color: #000 !important;
border: 1px solid #FF0000 !important;
}
div.matchCongratsPremGift {
display: none !important;
}
div#aiResultBox.matchCongratsQM {
display: none !important;
}

}
