css:
@-moz-document domain("customform.infinityfreeapp.com") {
.sheet-body {
    font-family: ui-sans-serif, -apple-system, BlinkMacSystemFont, Helvetica, "Apple Color Emoji", Arial, "PingFang SC", "Source Han Sans SC", "Noto Sans CJK SC", "Source Han Sans CN", "Noto Sans SC", "Source Han Sans TC", "Noto Sans CJK TC", "Hiragino Sans GB", "Microsoft YaHei", "Microsoft JhengHei", sans-serif, "Segoe UI Emoji", "Segoe UI Symbol"!important;
}

/* img[src="https://via.placeholder.com/150"] {
    opacity: 0;
}
 */

.undefined {
    width:20%
}

.side-text {
    height: 100%;
}

.text {
    position: absolute;
    top: calc(50% - 15px);
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    width: 100%;
    font-weight: bold;
    font-size: 150%
}

.cell > img {
    position: absolute;
    width: 100%;
    height: calc(100% - 30px);
    object-fit: cover;
    top: 0;
}

.cell-text span {
    transform: translateY(-1px);
}

.cell-text {
    position: absolute;
    font-size: .9em;
    width: 100%;
    height: 30px;
    border-top: 1px solid #ccc;
    bottom: 0;
    padding: 0;
}

.cell {
    position: relative;
    width: 100%;
    padding-top: calc(140% + 30px); 
    height: 0;
    border: black 1px solid;
    border-radius: 0;
}

.g-3, .gy-3 {
    --bs-gutter-y: .5rem;
}
.g-3, .gx-3 {
    --bs-gutter-x: .5rem;
    height: 100%;
}

.row.text-secondary.p-1 {
    margin-top:.5em;
    font-size: .8em;
}

.col-1 {
    width: 2em;
}

.col-11 {
    width: calc(100% - 2em);
}

h4 {
    margin-top: .5rem!important;
}

.mb-3 {
    margin-bottom: .5rem!important;
}
}
