css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("home.myspace.com") {
span{background-color:transparent !important;}
#header, #footer {display: none !important;}
h1,h2,h3,h4,h5{color:#FFFFFF !important;background-color:#000000 !important;}
div  {background-color : transparent !important; border:none !important;}
#home_profileInfo, div#home_profileInfo {background-color: #000000 !important;color:#000000 !important;}
#topnav, #home_additionalLinks, #home_userURLInfo, #home_setHomePage, #home_searchAddressBook, #splash_coolNewPeople, #splash_profile, #home_schools, h4.heading,#home_infoBar, #home_bulletins {display:none !important;}
body {background-color: #000000 !important;color:#000000 !important}
table, tr, td{background-color: #000000 !important;color:#000000 !important; border:0px !important;}
table.cols {background-color: #000000 !important;color:#000000 !important;}
.section {background-color:#000000 !important;color:#FFFFFF !important}
div.section {color: #FFFFFF !important; background-color: #000000 !important; border:0px !important; border-style: none !important; width:100% !important; margin-bottom:15px !important;}
a{text-decoration:none !important; font-weight:bold !important;color:#CCCCCC !important;}
a:link, a:active, a:visited{color:#CCCCCC !important;}
a:hover{color:#FFFFFF !important; text-decoration:none !important; background-color:transparent !important;}
}
