css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nu.nl") {
#masthead, #alt_nu, #extrasbox {
 display: none !important;
}
#photo {
 float: right !important;
 margin: 0.5em 0.5em 1em 1em !important;
}
/* slideshow */
#photos img {
 visibility: visible !important;
 position: relative !important;
}
.backgrPlayer {
 visibility: hidden;
}

/* slideshow in artikel */
.diaDivNav {
 background: #a8c4d7 !important;
}
a.btGeneral {
 background-image: none !important;
}
.titleDiaShow {
 border: none !important;
}
.diaDiv, .diaDivNav {
 margin: 0 auto !important;
}

/* artikel achtergrond */
.alg {
 background: #c8e4f7 !important;
}

/* icoontjes bij "zie ook" */
td.siteicon {
display: none !important;
}
}
