css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("entree.nu") {
.aanbod_detail img {
 float: right !important;
 clear: both !important;
}
/* stomme sfeer afbeelding */
#sfeerafbeeldingLeft, #sfeerafbeelding {
 display: none;
}
/* alles in een keer */
#details_kenmerken, #details_afbeeldingen {
 display: block !important;
}
#details_afbeeldingen * {
 display: inline;
}
#details_afbeeldingen img {
 clear: both;
 /*float: left;*/
}
/* de switcher is niet meer nodig */
#stappendetails {
 display: none;
}
/* geen twee keer dezelfde foto */
.kenmerken_foto {
 display: none;
}
#details_afbeelding img {
  height: 150px;
}
#details_afbeelding tr {
  display: inline;
}
#afbeeldingLayer {
 position: fixed !important;
 top: 50px !important;
}
.titelbalk-bottom-details {
clear: both;
}
}
