css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("salon.com") {

/* Set up background color */
#header, #masthead, #topbar, body, #nav2,#footer,#footer_inner,
#site_container,#below_fold,.box,.section_head,.banner,.blogs_inner,h2,.section_tabs,.scriptaculously,ul.tools
   {
   background-color:#c9e8f3 !important;
   background-image: none !important;
   border-top-color:black !important;
   }

a.drops {border-right-color:black !important;}

#blogs{color:#919BB8 !important;}

/* Various font colors */
a:link { color:#333399 !important; } 

a:visited  { color:#358442 !important; }

h2, .publish_date, .dateline { color:#485F23 !important; }

span.byline, div.byline, div.datetime, div.letters,#blogs .banner,.category ul.regular_features li.inside,h3,#most_read_tabbed ol li,.headline
   {color:black !important;}

/* Kill little red bullet, share */
ul.relateds li, li.share{background-image:none !important;}

/* Kill JS-based ads */
#ad_x10,#news{display:none!important}
}
