css:
@-moz-document domain('keptelenseg.hu') {
.gallerycontainer {
    counter-reset: imagenum;
}

.thumbnail .gallery {
    position: relative;
    counter-increment: imagenum;
}

.thumbnail .gallery::after {
    content: counter(imagenum);
    position: absolute;
    bottom: 0;
    right: 0;
    display: inline-block;
    background: rgba(0, 0, 0, 0.45);
    color: #FFFFFF;
    font-size: 15px;
    font-weight: 600;
    padding: 3px 10px;
    border-radius: 5px 0 0 0;
}
}
