css:
@-moz-document url-prefix('https://pixelfed.de/') {
    @media (prefers-color-scheme: dark) {
        :root {
            --light:#000;
            --dark: #fff;
            --body-bg: #000;
            --body-color: #eee;
            --nav-bg: #000;
            --bg-light: #212124;
            --primary: #008df5;
            --light-gray: #222;
            --text-lighter: #fff;
            --card-bg: #161618;
            --light-hover-bg: #212124;
            --btn-light-border: #161618;
            --input-border: #161618;
            --comment-bg: #212124;
            --border-color: #212124;
            --card-header-accent: #212124;
            --dropdown-item-hover-bg: #000;
            --dropdown-item-hover-color: #818181;
            --dropdown-item-color: #64748b;
            --dropdown-item-active-color: #fff
        }
    }

    .force-dark-mode {
          --light:#000;
          --dark: #fff;
          --body-bg: #000;
          --body-color: #eee;
          --nav-bg: #000;
          --bg-light: #212124;
          --primary: #008df5;
          --light-gray: #222;
          --text-lighter: #fff;
          --card-bg: #161618;
          --light-hover-bg: #212124;
          --btn-light-border: #161618;
          --input-border: #161618;
          --comment-bg: #212124;
          --border-color: #212124;
          --card-header-accent: #212124;
          --dropdown-item-hover-bg: #000;
          --dropdown-item-hover-color: #818181;
          --dropdown-item-color: #64748b;
          --dropdown-item-active-color: #fff
    }

    .text-body {
        color: #fff!important
    }

    .text-muted {
        color: #eee!important
    }

    .text-black-50 {
        color: hsla(0,0%,100%,.5)!important
    }

    .text-white-50 {
        color: rgba(0,0,0,.5)!important
    }
}
