css:
@-moz-document url-prefix("http://github"),
url-prefix("http://github"),
domain("github.com") {
  /* 
* Ù°Vazir Font by Saber Rastikerdar - https://github.com/rastikerdar/vazir-font
*/

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir.eot?#iefix') format('embedded-opentype'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir.woff2') format('woff2'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir.woff') format('woff'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir.ttf') format('truetype');
    font-weight: normal;
  }

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Bold.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Bold.woff2') format('woff2'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Bold.woff') format('woff'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Bold.ttf') format('truetype');
    font-weight: bold;
  }

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Light.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Light.woff2') format('woff2'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Light.woff') format('woff'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Light.ttf') format('truetype');
    font-weight: 300;
  }

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Medium.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Medium.eot?#iefix') format('embedded-opentype'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Medium.woff2') format('woff2'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Medium.woff') format('woff'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Medium.ttf') format('truetype');
    font-weight: 500;
  }

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Thin.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Thin.eot?#iefix') format('embedded-opentype'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Thin.woff2') format('woff2'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Thin.woff') format('woff'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Thin.ttf') format('truetype');
    font-weight: 100;
  }

  @font-face {
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Black.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Black.eot?#iefix') format('embedded-opentype'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Black.woff2') format('woff2'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Black.woff') format('woff'),
      url('https://cdn.rawgit.com/rastikerdar/vazir-font/v20.1.0/dist/Vazir-Black.ttf') format('truetype');
    font-weight: 900;
  }


  .task-list-item-checkbox {
    margin: 0 0 0 0.2em !important;

  }

  [data-test-id="comment-body"] {
    direction: rtl !important;
    font-family: "Vazir" !important;
  }

  [data-test-id="side-panel-title-content"],
  [data-test-id="card-side-panel-trigger"],
  .markdown-body {
    font-family: "Vazir" !important;
  }

  .Box {
    direction: ltr !important;
  }

  .kMdupH {
    direction: rtl !important;
    text-align: right !important;
  }

  .iVwhFC {
    margin-right: 0 !important;
    margin-left: 4px !important;
  }

  .hzMsHa {
    margin-right: 4px !important;
    margin-left: 0 !important;
  }
}
