css:
@-moz-document 

url(chrome://bookmarks/content/bookmarks.html),
url-prefix(chrome://bookmarks/content/bookmarks.html?) {


html,body,div,span,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,abbr,address,cite,code,del,dfn,em,img,ins,kbd,q,samp,small,strike,strong,sub,sup,var,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,input,button,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,dialog,figure,footer,header,hgroup,nav,section,time,mark,audio,video{margin:0 !important;padding:0 !important;border:0 !important;outline:0 !important;font-weight:inherit !important;font-style:inherit !important;font-size:100% !important;font-family:inherit !important;vertical-align:baseline !important;background:transparent !important}body{line-height:1 !important}ol,ul{list-style:none !important}table{border-collapse:separate !important;border-spacing:0 !important}caption,th,td{text-align:left !important;font-weight:normal !important}blockquote:before,blockquote:after,q:before,q:after{content:"" !important}blockquote,q{quotes:"" "" !important}article,aside,dialog,figure,footer,header,hgroup,nav,section{display:block !important}input,select{vertical-align:middle !important}


body {
  font:62.5% arial, sans-serif !important;
  background: #3F3F3F !important;
}

a:hover,
a:active {
  outline: none;
}

.content {
  padding: 5px 0 !important;
  margin: 20px auto !important;
  border: 1px solid #fff;
  background: #f5f5f5;
  background: -moz-linear-gradient(top, #fff, #F7F7F7) !important;
  -moz-border-radius: 5px;
  -moz-box-shadow: 0 1px 5px #000;
  overflow: hidden !important;
}


.ellipsis {
  margin: 0 -1px !important;
  line-height: normal !important;
}
.ellipsis div:first-child {
  float: none !important;
}
.ellipsis > div + div {
  display: none !important;
}
.ellipsis a {
  display: block !important;
  padding: 5px 15px !important;
  color: #222 !important;
  font-size: 1.45em !important;
}
.ellipsis a:hover {
  color: #fff !important;
  background: -moz-linear-gradient(top, #0C79FF, #014ACD) !important;
  text-decoration: none !important;
  text-shadow: 1px 0 2px rgba(0,0,0,.85) !important;
}
img {
  margin: 0 9px 0 0 !important;
  vertical-align: middle !important;
}

hr {
  margin: 5px 14px !important;
  border: 0 !important;
  border-bottom: 1px solid #BFBFBF !important;
}

}
