css:
@-moz-document domain(saito.io) {

:root {
    --saito-font: "Inter";
}

.saito-calendar-weekdays div {
    font-size: 2.1rem;
    text-align: left;
    font-weight: 600;
    padding-bottom: 13px;
}

.saito-button-primary:hover {
    background: #e8001e;
    color: #ffffff;
}

.saito-menu>ul>li:hover, .saito-menu>ul>li:hover>i {
    background-color: #ffffff;
    color: var(--saito-primary);
}

.saito-menu li:hover i::before {
    -webkit-text-stroke: var(--saito-primary);
}

  .saito-menu>ul>li:hover, .saito-menu>ul>li:hover>i {
    background-color: var(--saito-background-color);
    color: var(--saito-primary);
}
  
.saito-menu i::before {
    -webkit-text-stroke: var(--saito-primary);
}

.saito-menu i::before {
    -webkit-text-stroke: var(--saito-primary) 1px;
    color: var(--saito-primary);
    font-size: smaller;
}

.saito-menu li:hover i::before {
    -webkit-text-stroke: var(--saito-primary);
}

.saito-menu i::before {
    -webkit-text-stroke: var(--saito-primary) 1px;
}

.tweet {
    border-radius: 20px;
}

.saito-table-row {
  transition: none;
}

.saito-address {
    font-weight: 700;
    color: #f71f3d;
}

.saito-userline {
    padding-top: 2px;
    color: #818181;
}

.tweet-controls>div:hover {
    transform: none;
    color: var(--saito-primary);
}

.tweet.has-reply::after {
  border-left: 2px dotted var(--saito-border-color);
}

.tweet-text {
    line-height: 2.3rem;
}

.saito-menu>ul>li {
    transition: none;
}
}
