css:
@-moz-document domain("prog.hu"), domain("pcforum.hu") {
/* HEADER */
ul.crumbs .title {
    font-weight: 300;
    font-size: 1.5em;
}

ul.crumbs li a:not(.url) {
    text-transform: uppercase;
}

ul.crumbs li:after {
    content: "\f054";
    font-family: 'FontAwesome';
    vertical-align: middle;
    color: #EAEAEA;
}

ul.crumbs li:last-child:after {
    display: none;
}

BODY.page-home ul.crumbs li a:visited {
    color: #000 !important;
}

#backtotopvert {
    backdrop-filter: blur(8px);
}

#backtotopvert .centercol {
    width: 100%;
}

#pagesearch form:before {
    border: none;
    border-radius: 3px;
}

BODY.nonzeroscroll:not(.lastscrolledup) .pageheader-container-align {
    box-shadow: 0px 0.5em 1em -0.7em rgb(0 0 0 / 50%);
}

body,
.pagebody .centercol,
.maincol .sideframe > .configcontrol,
.configcontrol > .fo:before,
.configcontrol > .fo:after,
.sideframe > .title .fo,
.related.withthumbs ul li .title
{
    background-color: #f8f8f8;
}

.pagelogo {
    background-color: transparent;
}

.popupmenu ul {
    overflow: hidden;
    border-radius: 3px;
}

.popupmenu,
.popupmenu .contents {
    background: none !important;
    padding: 0;
}

.popupmenu {
    overflow: visible !important;
}

#pagemenu > .popupmenu > .contents > .align > * {
    padding: 0;
}

.pagetagbar {
    text-align: center;
}

.pagetagbar ul li:last-child::after {
    display: none;
}

.pagetagbar a {
    font-weight: 600;
    text-transform: uppercase;
    font-size: 0.9em;
}

.regularnews .widethumbs > li {
    border: none;
}

.maincol {
    overflow: visible;
}

.maincol .previewwiththumb {
    background: #ffffff;
    border-radius: 3px;
    overflow: hidden;
    /* box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px; */
    box-shadow: rgba(0, 0, 0, 0.24) 0px 0px 3px;
    transition: all 0.3s;
    height: 100%;
}

.maincol .previewwiththumb .thumboverlay {
    transition: background-color 0.3s;
}

.maincol .previewwiththumb img {
    transition: transform 0.3s;
    transform: scale(1.02) rotate(1.0deg) translateY(-5px);
}

.maincol .previewwiththumb:hover {
    box-shadow: rgba(0, 0, 0, 0.25) 0px 14px 28px, rgba(0, 0, 0, 0.22) 0px 10px 10px;
}

.maincol .previewwiththumb:hover .thumboverlay {
    background-color: transparent;
}

.maincol .previewwiththumb:hover img {
    transform: scale(1.1) rotate(0deg);
}


.maincol .previewwiththumb .texts {
    margin-inline: 0;
    padding: 10px !important;
    background-color: #FFFFFF;
}

.maincol .previewwiththumb .title {
    box-shadow: none !important;
    margin: 0 !important;
    display: block !important;
}

.config-viewmode-normal .widethumbs .thumb img,
.widethumbs .thumb img {
    border: none !important;
}

.widethumbs > ul {
    width: 100%;
}

.widethumbs > li {
    margin-block-end: 25px !important;
}

.blockwithside:not(.focus) > *:last-child {
    display: none;
}

.blockwithside.focus .title {
 overflow: hidden;
   text-overflow: ellipsis;
   display: -webkit-box !important;
   line-height: 1.4em;
   max-height: 2.8em;
   -webkit-line-clamp: 2;
   -webkit-box-orient: vertical;
}

.previewwiththumb.home.withimage.ext.sunken.widethumbs {
    padding: 0;
}

.blockwithside > *:last-child {
    border: none;
}

.maincol .blockwithside {
    width: 100%;
}

.blockwrapper:after {
    border: none;
}

.sidelist > ul > li:before {
    content: '';
}

.sidelist > ul > li:hover a {
    color: orange;
}

.sideframe > .title .fo {
    text-transform: uppercase;
}

#sidecol .sidelist > ul > li {
    margin-block: 0.4em;
}

.allheader:after,
.sectionline
{
    border-bottom: 1px solid #EDEDED !important;
}

.related {
    border-top: 1px solid #EDEDED !important;
}

#topforums,
#topkbases {
    margin-block: 2em;
}

.sectiontitle .long {
    display: none;
}

.shortkbaselist > ul > li .title {
    margin-inline-start: 0.3em;
}

.shortkbaselist > ul > li:before {
    font-family: 'FontAwesome';
    font-size: 100% !important;
    left: 0 !important;
    top: 0.4em !important;
    
}

.shortkbaselist > ul > li.accepted:before {
    content: '\f058';
    color: #91cc48;
}

.shortkbaselist > ul > li.answered:before {
    content: '\f1db';
    color: #d5f1ff;
}

.shortkbaselist > ul > li.opened:before {
    content: '\f1db';
}


/* Forum entries */
.maincol .objpreviewalign {
    margin-bottom: 3.5em;
}

.forumentry {
    margin-block: 3.5em;
}

.forumentry .inner {
    border-radius: 2px;
    /*overflow: hidden;*/
    /*background-color: #FBFBFB;*/
    overflow: visible;
    background-color: #FFFFFF;
    box-shadow: rgba(0, 0, 0, 0.24) 0px 1px 3px;
}

.forumentry .contentcontainer {
    padding: 0 0.5em !important;
}

.forumentry:not(.topquestion):not(.preview) .contentcontainer {
    margin-block-start: 1em;
}

.forumentry.topquestion .inner,
.forumentry.preview .inner{
    margin-block: 0em 2.5em !important;
    padding: 1.0em;
}

.forumentry.modermsg .inner {
    box-shadow: rgba(255, 0, 0, .3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 2px 6px 2px;
}

.forumentry.modermsg > .inner:after,
.forumentry.question > .inner:after
{
    content: '' !important;
}

.forumentry > .inner > .actions {
    padding-block-start: 0.2em;
    margin-block-start: 1em;
    border-block-start: 1px solid rgba(0, 0, 0, 0.05);
    text-align: center;
}

.msg_quote {
    background-color: rgba(0, 0, 0, 0.05);
    border-radius: 3px;
    padding: 0.5em 1em !important;
    margin-inline-start: 54px !important;
    background-image: none !important;
}

.msg_quote::after {
    position: absolute;
    left: -54px;
    top: 0;
    display: inline-block;
    content: ' ';
    background-image: url('https://www.prog.hu/common/images/quoteicon.png');
    background-repeat: no-repeat;
    width: 54px;
    height: 54px;
}

.CODEDIV {
    border-radius: 3px;
    border-inline-start: 4px solid #6cf;
    background-color: rgba(0, 0, 0, 0.05);
    padding:  0.5em 1.0em;
    font-size: 0.9em;
    line-height:1.3;
}

.CODEDIV .fcodeblock {
    font-family: ui-monospace,"Cascadia Mono","Segoe UI Mono","Liberation Mono",Menlo,Monaco,Consolas,monospace !important;
}

.expandable-control.expanded .CODEDIV {
    max-height: none;
}

.forumentry > .inner > ul {
    padding-left: 6.25em;
}

.forumentry .thumb,
.listentry .thumb {
    margin-top: -2em;
    border: none;
    box-shadow: rgba(0, 0, 0, 0.16) 0px 1px 4px;
    padding: 0;
    border-radius: 3px;
    overflow: hidden;
    background: #FFFFFF;
}

.forumentry .timestamp,
.listentry .timestamp {
    display: block !important;
    margin-block: 0.4em 0 !important;
    font-weight: 300;
    /* float: right; */
}

.forumentry .thumb img,
.listentry .thumb img
{
    width: 4.75em;
}

.forumentry .nickname,
.listentry .nickname
{
    font-weight: 600;
}


.forumentry .permalink {
    position: absolute;
    top: 5px;
    right: 5px;
}

.configcontrol > .popup {
    border-radius: 3px;
    border: none;
    box-shadow: 0 5px 5px -3px rgb(0 0 0 / 20%), 0 8px 10px 1px rgb(0 0 0 / 14%), 0 3px 14px 2px rgb(0 0 0 / 12%);
}

.visited .previewwiththumb .thumb .thumboverlay:after {
    background-color: rgba(136, 136, 136, 0.4);
    border-radius: 3px;
    text-shadow: 1px 1px 3px rgba(0,0,0,0.25);
}

.videoimg .thumb .thumboverlay:before {
    left: 50%;
    top: 50%;
    font-size: 3em;
    transform: translate(-50%, -50%);
}

.contentbody > h1, .contentbody > h2, .contentbody > h3, .contentbody > h4, .contentbody > h5, .contentbody > h6, .contentbody > p, .contentbody > div, .contentbody > ul, .contentbody > ol, .contentbody > code, .contentbody > table, .contentbody video {
    float: none;
    max-width: none;
}

/* Hozzászólás szerkesztő */
.tox-editor-container,
.tox-tinymce {
    overflow: visible !important;
}

.tox-editor-container {
    border-radius: 3px;
    border: 1px solid #D0D0D0 !important;
}

.tox-tinymce {
    height: auto !important;
}

.tox-edit-area {
    height: 50px !important;
    transition: all ease-in-out 0.2s;
}

.tox-edit-area.focused {
    height: 400px !important;
}

.tox-sidebar-wrap {
    padding: 0.5em !important;
    background-color: #fcfcfc !important;
}

.tox:not([dir=rtl]) .tox-toolbar__group:not(:last-of-type) {
    border-color: #e0e0e0;
}

.tox .tox-tbtn--disabled {
    color: rgba(34,47,62, 0.1);
}

.tox .tox-edit-area {
    overflow: hidden;
    border: 0 !important;
}

.tox .tox-toolbar__primary {
    justify-content: center;
}

.buttonline.single > * {
    text-align: center;
}

.actionbutton {
    transition: all 0.2s;
    font-weight: 600;
    text-transform: uppercase;
    box-shadow: 0 2px 3px rgb(0 0 0 / 12%), 0 1px 2px rgb(0 0 0 / 24%);
    border-color: #9c3;
}

.actionbutton:hover {
    box-shadow: 0 3px 6px rgb(0 0 0 / 16%), 0 3px 6px rgb(0 0 0 / 23%);
    border-color: orange;
}

/***/

/* Videó nem kattintható */
.contentbody figure, .contentbody .figure, .contentbody > .center > .illu, .contentbody > .center > video, .contentbody > .center > iframe, .contentbody > video {
    float: none;
}

@font-face {
    font-family: "Segoe UI";
    font-weight: 300;
    src: local("Segoe UI Light");
    font-display: swap
}

.lead {
    font-weight: 300 !important;
}

.previewwiththumb .title {
    font-weight: 600;
}

/* ADS */
/*.maincol .zone + div:not(.blockwrapper),
.adsense,
ins,
.adsbygoogle,
.sharebarcontainer,
#zone-sponsorright,
.adzonesponsorrightalign,
.adzonesponsorleftalign,
.adzonesponsortopalign,
.sponsor,
#subscribebar
{
    display: none !important;
}*/
/****/

/* NETWORK HEADER */
/*.networkheader {
    display: none;
}
BODY.fixedtop .pageheader-container-align {
    top: 0 !important;
}*/
/***/
}
