css:
@-moz-document url-prefix(https://scratch.mit.edu) {
:root {
  --accent: /*[[accent]]*/;
  --link: /*[[link]]*/;
  --hover: /*[[hover]]*/;
  --bg: /*[[bg]]*/;
  --black: /*[[black]]*/;
  --second: /*[[second]]*/;
  --banner: /*[[banner]]*/;
}
.innerwrap {
  border-bottom: var(--accent) 1px solid !important;
  background-color: var(--bg) !important;
}
#navigation {
  border-bottom: var(--accent) 1px solid;
  background-color: var(--bg);
  border-top: var(--accent) 1px solid;
}
#view {
  background-color: var(--black);
}
.box-header {
  background-color: var(--black) !important; 
  border-top: none !important;
}
.box {
  border: none !important;
    background-color: var(--bg) !important;
   outline: var(--accent) 1px solid !important;
}
.banner-button> span{
  color: white !important;
}
.action-button > span {
  color: white !important;
}
.message-count {
  background-color: var(--accent) !important;
}
#brdheader {
  background-color: var(--banner) !important;
}
#better-change-featured-project {
  background-color: var(--accent);
}
.action-button {
  background-color: var(--accent) !important;
}
#topnav {
  border-top: var(--accent) 1px solid !important;
}
.news-description > h4 {
  color: var(--accent) !important;
}
.inner > .box > .box-content {
  background-color: var(--bg);
  box-shadow:  0 0 0 20px var(--black);
  border-top: var(--accent) 1px solid !important;
}
.active {
  color: white !important;
  background-color: var(--accent) !important;
}
.see-inside-button> span {
  color: white !important;
}
.remix-button > span {
  color: white !important;
}
.compose-post > span{
  color: white !important;
}
.comment-reply > span{
  color: white !important;
}
.reply > span{
  color: white !important;
}
.comment-reply:after {
  background-image: url(https://svgur.com/i/psH.svg) !important;
  filter: none !important;
}
.reply:after {
  background-image: url(https://svgur.com/i/psH.svg) !important;
  filter: none !important;
}
.title-banner{
  background-color: var(--banner) !important;
  border-bottom: var(--accent) 1px solid;
}
.box-head > h2{
  background-color: var(--banner) !important;
  box-shadow:  0 0 0 500px  var(--banner);
}
.messages-social-list > .social-message  {
  border: var(--accent) 1px solid;
}
.messages-social-list {
    border: none !important;
}
.messages-header-unread {
  background-color: var(--accent);
}
.show {
  background-color: var(--accent);
}
.pulldown {
  color: var(--accent) !important;
}
a {
  color: var(--link) !important;
}
#sidebar {
  padding: 0px !important;
}
a:hover {
  color: var(--hover) !important;
}

#sidebar > ul > li {
  border-left: var(--accent) 1px solid;
  border-right: var(--accent) 1px solid;
}

button > span {
  color: var(--accent);
}
.text {
  color: var(--accent);
}
.button {
  background-color: var(--accent);
}
.small.grey.button.enabled:hover {
  background-color: var(--hover);
}
span > a {
  color: var(--link) !important;
}
.media-control-edit{
  background-color: #181818 !important;
}
.grey.button {
  background-color: var(--black) !important;
}
.grey.button:hover {
  background-color: var(--second) !important;
}
.button.small.grey {
  background-color: var(--bg) !important;
}
form > .button {
  background-color: transparent!important;
}
#tabs > .active {
  background-color: var(--bg) !important;
  border-bottom: var(--hover) 3px solid !important;
}
.sub-list.active > .active {
  background-color: var(--bg) !important;
}
.sub-list > .active {
  color: var(--accent) !important;
  background-color: #282828 !important;
}
.active > a {
  color: white !important;
}
#change-country > button {
  background-color: var(--accent);
}
.first.active > .active {
  background-color: #282828 !important;
  color: var(--accent) !important;
}
.col-12 > .box {
  padding: 10px !important;
}
.tabs-index {
  padding: 10px !important;
}
.button.small {
  background-color: var(--accent);
}
.button.small > a {
  color: white !important;
}
.compose-cancel > span {
  color: white !important;
}
.post-link > button {
  background-color: var(--accent) !important;
}
form > button {
  background-color: var(--accent) !important;
}
}
