css:
@-moz-document url-prefix(https://yugioh.party/) {
div, .navbar-inverse .navbar-brand, .panel-default>.panel-heading, .btn-default, .form-control {
  color: white;
}

.form-group p {
  color: white !important;
}

.panel-heading a {
  font-weight: bold;
  color: #0089ff !important;
}

 table, .panel {
  background-color: #242526 !important;
}

body, input {
  background-color: #313131 !important;
}

nav, .panel-heading, .panel-footer, .btn-default, thead {
  background-color: #18191A !important;
}
}
