css:
@-moz-document domain('4channel.org'), domain('4chan.org'), domain('boards.4channel.org'), domain('boards.4chan.org') {
.bottomCtrl, .middlead {
	margin-top: 15px;
	float: none;
}

#ctrl, #ctrl-top, #threads, .board, #boardNavDesktop, div.navLinks:not(.navLinksBot), .is_arclist div.navLinksBot, span.navLinksBottom, .boardList, #arc-list, #boardNavDesktopFoot, .bottomCtrl, #styleSwitcher {
	width: 70% !important;
	margin-left: auto !important;
	margin-right: auto !important;
}

#ctrl, #ctrl-top, .navLinks, .boardBanner {
	margin-top: 20px;
}

#ctrl-top, .navLinks {
	margin-bottom: 20px;
}

hr, #toggleMsgBtn {
	display: none;
}
  
#blotter {
	margin-top: 10px;
}
  
.board hr {
	display: inherit;
}

.navLinksBottom {
	display: block;
}

.open-qr-wrap {
	position: inherit;
    text-align: inherit;
    margin-right: auto;
    margin-left: 0;
}

div.pagelist {
	margin-left: auto;
    margin-right: auto;
    width: 70%;
    margin-top: 10px;
    float: none;
    display: flex;
    justify-content: center;
}

.closed {
    padding: 20px !important;
}

.stylechanger {
	float: right;
}

#styleSwitcher {
    float: none;
    display: flex;
    justify-content: flex-end;
}

#styleSwitcher select {
    margin-left: 4px;
}
}
