css:
@-moz-document domain("news.ycombinator.com") {
:root {
  --c-primary:  #0F1226;
  --c-primary-l1: #1d234c;
  --c-primary-l2: #9498af;
  --c-primary-l3: #1d2136;
  --c-secondary: #F7EDE2;
  --c-secondary-d1: #999189;
  --c-secondary-l1: #e9ceb0;
  --c-tertiary: #FF6600;
  --m-width: 760px;
  --b-radius: 8px;
  --mono-font: ui-monospace, "Jetbrains Mono", "Fira Code", "Cascadia Code", "Menlo", "Monaco", "Cascadia Mono", "Segoe UI Mono", "Roboto Mono", "Oxygen Mono", "Ubuntu Monospace", "Source Code Pro", "Fira Mono", "Droid Sans Mono", "Courier New", "Consolas", monospace;
}

body {
  color: #F7EDE2 !important;
}

body, #hnmain {
  background-color: var(--c-primary) !important;
}

textarea {
  font-family: var(--mono-font);
  font-size: 1rem;
  background: var(--c-primary-l1);
  color: var(--c-secondary);
  padding: 10px 10px;
  border: none;
  border-radius: var(--b-radius);
  width: 100% !important;
  max-width: 100% !important;
}

input {
  background: var(--c-primary-l1);
  color: var(--c-secondary-l1);
  padding: 5px 10px;
  border: none;
  border-radius: var(--b-radius);
  font-family: var(--mono-font);
}

input[type=submit] {
  font-family: var(--mono-font);
  background: var(--c-primary-l1);
  color: white;
  padding: 10px 20px !important;
  border-radius: var(--b-radius);
  border: none;
  text-transform: uppercase;
  font-size: 0.7rem;
  cursor: pointer;
  transition: background-color 0.1s ease-in-out;
}

input[type=submit]:hover {
  background: var(--c-tertiary);
}

.hnname {
  display: inline-block;
  margin-right: 40px;
}

#hnmain {
  max-width: var(--m-width) !important;
  margin-left: auto;
  margin-right: auto;
  min-width: 500px !important;
}

#hnmain > tbody > tr:nth-child(1) > td {
  background-color: var(--c-primary) !important;
  width: var(--m-width) !important;
}

a:link {
  color: var(--c-secondary) !important;
}

a:visited {
  color: var(--c-secondary-d1) !important;
}

a.morelink {
    padding: 10px 30px;
    display: inline-block;
    background: var(--c-primary-l1);
    border-radius: var(--b-radius);
  transition: background-color 0.1s ease-in-out;
}

a.morelink:hover {
  background: var(--c-tertiary);
}

a.morelink:visited {
  color: var(--c-secondary) !important;
}

#hnmain > tbody > tr:nth-child(1) {
  padding: 12px 0;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: var(--c-primary) !important;
}

#hnmain > tbody > tr:nth-child(3) {
  display: block;
  margin-top: 10px;
}

.pageton a, .pagetop a:visited {
  color: var(--c-secondary) !important;
}

.titleline {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 10px;
  margin-bottom: 10px;
  font-family: var(--mono-font) !important;
  font-size: 1.1rem !important;
}

.titleline a {
  transition: color 0.1s ease-in-out;
}

.athing:hover .titleline a {
  color: var(--c-tertiary) !important;
}

tr.spacer {
  height: 20px !important;
}

.subline {
  color: var(--c-primary-l2) !important
}

.subline .hnuser, .sitestr {
  color: var(--c-secondary-l1) !important;
}

.score {
  color: var(--c-secondary-l1);
}

.subtext {
  font-family: Inter !important;
  font-size: 0.8rem !important;
  opacity: 0.4;
  transition: opacity 0.1s ease-in-out;
}

.subtext:hover {
	opacity: 1;
}

.athing:hover + tr .subtext {
  opacity: 1;
}

.votelinks {
	display: inline-block;
  padding: 4px 6px 9px;
  transform: scale(1.1);
  opacity: 0.6;
}

.rank {
  font-family: var(--mono-font);
  font-size: 1.2rem;
  color: var(--c-secondary-l1);
  opacity: 0.3;
  font-weight: 800;
}

#hnmain > tbody > tr:nth-child(3) > td > div {
    color: var(--c-primary-l2) !important;
}

.toptext {
  color: var(--c-secondary);
  font-size: 1rem;
  line-height: 1.3;
  margin: 20px auto;
}

.comment-tree .comment .commtext {
  color: var(--c-secondary);
  font-size: 0.9rem;
  line-height: 1.3;
}

.comment .commtext {
  color: var(--c-secondary);
  font-size: 0.9rem;
  line-height: 1.3;
}

.commtext a:link {
  color: var(--c-secondary-l1) !important;
  transition: color 0.1s ease-in-out;
  text-decoration: none;
}

.commtext a:hover {
  color: var(--c-tertiary) !important;
}

.commtext pre {
    background: var(--c-primary-l3);
    padding: 15px 20px;
    font-family: var(--mono-font);
    border-radius: var(--b-radius);
    line-height: 1.4;
}

.comhead {
    opacity: 0.6;
    font-size: 0.7rem;
    font-family: var(--mono-font);
  	flex-shrink: 0;
}

.athing:hover .comhead {
    opacity: 1;
}

.yclinks a {
  color: var(--c-secondary-l1) !important;
  opacity: 0.4;
  transition: opacity 0.1s ease-in-out;
}

.yclinks:hover a {
  opacity: 1;
}

.yclinks a:hover {
  color: var(--c-tertiary) !important;
}

.pagetop a:hover {
  color: var(--c-tertiary) !important;
}
}
