css:
@-moz-document domain("freesteam.ru"), url-prefix("https://freesteam.ru"), regexp("freesteam")

{
/*	Main	*/
body {
    background: #32465a;
}

.archive .page-header, .search .page-header, .no-results .page-header, .error-404.not-found .page-header, .hentry, .widget, #comments, .navigation.posts-navigation, .search.search-no-results .page-content, .error404 .error-404.not-found .page-content, .error404 ul.posts-container, .search.search-no-results ul.posts-container, #gotop, #infinite-handle span {
    background: #9aa9b9;
}

.hentry .entry-footer, .hentry .entry-footer a, .hentry .entry-author a, .single .single-posted-on, .single .single-posted-on a {
    background: #9aa9b9;
     color: #000000 !important;
    font-size: 12px !important;
    font-family: helvetica neue,Helvetica,Arial,sans-serif;
}

.taxonomy-description p {
     color: #ff0000 !important;
    font-size: 16px !important;
}

#disqus_thread,
#thread__container,
.footer-bottom,
#colophon,
.banner-fs,
.entry-comments,
.author-meta {
	display: none !important;
}

.site-main .post-navigation {
    border-top: 1px solid #f7f9f9;
    background: #2c3e50;
    margin-top: -34px;
    padding: 30px 45px;
}

}
