/* ==UserStyle==
@name           ROBLOX Late 2014/Early 2015 Sign Up Page
@namespace      idk lol
@version        1.0.0
@description    all the code by melongirl
@author         iloverobloacsks2214 (flinticarft)
==/UserStyle== */

    @-moz-document url("https://www.roblox.com/"),
    url("https://www.roblox.com"),
    url-prefix("https://www.roblox.com/?returnUrl=") {
    /* Sign Up Landing */
    .lower-logo-container {
        background-color: transparent!important;
        min-height: 872px !important;
    }
    .container-footer {
        z-index: 3;
        background-color: #ffffff!important;
    }
    @font-face { font-family: "Source Sans Pro"; src: local("Source Sans Pro Light"), local("SourceSansPro-Light"), url("http://fonts.gstatic.com/s/sourcesanspro/v9/toadOcfmlt9b38dHJxOBGN5zCZmJHbPt3vUI1beFfvf3rGVtsTkPsbDajuO5ueQw.woff2") format("woff2"); font-style: normal; font-weight: 300; unicode-range: U+0-FF, U+131, U+152-153, U+2C6, U+2DA, U+2DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000; }
    @font-face { font-family: "Source Sans Pro"; src: local("Source Sans Pro"), local("SourceSansPro-Regular"), url("http://fonts.gstatic.com/s/sourcesanspro/v9/ODelI1aHBYDBqgeIAH2zlPytCVloACqnDHJwh-em2k8.woff2") format("woff2"); font-style: normal; font-weight: 400; unicode-range: U+0-FF, U+131, U+152-153, U+2C6, U+2DA, U+2DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000; }
    .lower-logo-container {
	    padding-top: 20px;
    }
    #RollerContainer.rollercoaster-background {
        background-image: url('http://images.rbxcdn.com/dcbdfaf1c08058e71f65c09f7b98ff04.jpg')!important;
    }
    #InnerRollerContainer {
	    background-image: none;
    }
    .lower-logo-container {
	background-color: transparent;
    }
    #signup {
        
    }
    .signup-container.rbx-login-form {
        background-color: rgba(255,255,255,0.75)!important;
        padding: 15px;
        max-width: 405px;
        border-radius: 0px;
        position: relative;
        left: 200px;
    }
    .signup-header {
        font-size: 24px!important;
        margin-top: 5px;
        margin-bottom: 10px;
        text-align: left;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif!important;
        text-transform: unset;
        font-weight: 300!important;
        color: rgb(71, 71, 71)!important;
    }
    .legal-text-container, .font-caption-header {
        display: none!important;
    }
    .dark-theme .input-field {
        color: #555!important;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif!important;
        background-color: #fff!important;
        border: 1px solid #ccc;
        box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
        transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
        height: 49px!important;
        padding: 10px 16px;
        font-size: 20px;
        border-radius: 6px;
    }
    .dark-theme .input-field:focus {
        box-shadow: inset 0 1px 1px rgba(0,0,0,0.075),0 0 8px rgba(102,175,233,0.6);
        border-color: #66afe9;
        outline: 0;
    }
    .dark-theme .input-field::placeholder {
        color: #555!important;
    }
    .has-error .input-field, .has-error .form-control.birthday-select-group {
        border-color: #843534!important;
        box-shadow: inset 0 1px 1px rgba(0,0,0,0.075),0 0 6px #ce8483!important;
    }
    .has-success .input-field, .has-success .form-control.birthday-select-group {
        border-color: #2b542b!important;
        box-shadow: inset 0 1px 1px rgba(0,0,0,0.075),0 0 6px #67b168!important;
    }
    .signup-container .birthday-container .birthday-select-group, .dark-theme .fake-input-lg {
        padding: 6px 12px;
        color: #555;
        background-color: #fff;
        box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
        border: 1px solid #ccc;
        height: 46px;
    }
    .signup-container .birthday-container .birthday-select-group:before {
        content: "Birthday";
        font-size: 18px;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        font-weight: 300;
        display: inline-block;
        margin-bottom: 5px;
        padding: 5px 8px;
        line-height: 23.9333px!important;
    }
    .signup-container .birthday-container .rbx-select-group.month {
	    border-right: 1px solid #ccc;
        border-left: 1px solid #ccc;
	    border-radius: 0px;
        width: 97px;
    }
    .signup-container .birthday-container .rbx-select-group .rbx-select {
        min-width: unset;
    }
    .rbx-select-group .rbx-select {
        background-color: transparent!important;
        color: #555!important;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        font-size: 18px!important;
        font-weight: 300;
        padding-left: 3px;
        padding-right: 3px;
        padding-bottom: 1px;
        background-image: url('http://images.rbxcdn.com/379f4f1018f31cbb62ef52a22d9f2118.png')!important;
        background-position: 92% 40%!important;
        text-indent: 0.01px!important;
        line-height: 23.9333px!important;
    }
    #MonthDropdown.rbx-select {
        width: 97px;
        margin-left: 2px;
    }
    #DayDropdown.rbx-select, .signup-container .birthday-container .rbx-select-group.day {
        width: 56px;
        margin-left: 2px;
    }
    #YearDropdown.rbx-select, .signup-container .birthday-container .rbx-select-group.year {
        width: 60px;
        margin-left: 2px;
    }
    .signup-container .birthday-container .rbx-select-group:nth-of-type(2) .rbx-select {
        border-right: 1px solid #ccc!important;
    }
    .signup-container .birthday-container .rbx-select-group:first-of-type .rbx-select {
        border-right: none!important;
    }
    .gender-container .fake-input-lg {
        
    }
    .gender-container .fake-input-lg:before {
        content: "Gender:";
        font-size: 18px;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        font-weight: 300;
        display: inline-block;
        margin-bottom: 5px;
        padding: 5px 8px;
        line-height: 23.9333px!important;
        position: relative;
    left: -115px;
    top: -13px;
    }
    .signup-container .gender-button, .signup-container .gender-button:focus {
        width: 32px;
        height: 32px;
        border: none!important;
    }
    .gender-female, .gender-male {
        background-position: 8px -4px !important;
        background-repeat: no-repeat;
        border-radius: 15px!important;
        border: 1px solid #ccc;
        width: 32px!important;
        height: 32px!important;
    }
    .gender-icon {
	    margin: 3px 9px;
	    height: 26px;
    }
    .gender-female {
        background-image: url('data:image/png;base64,');
        width: 13px;
        position: relative;
        left: -9px;
        top: -2px;
    }
    .gender-male {
        background-image: url('data:image/png;base64,');
        width: 13px;
        position: relative;
        left: -9px;
        top: -2px;
    }
    .gender-icon.gender-selected {
	    background-color: #255094;
        border: 1px solid #255094;
    }
    #FemaleButton {
        margin-left: 95px;
    }
    #FemaleButton, #MaleButton {
        margin-top: 4px;
    }
    #signup-button, #main-login-button {
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        background-color: #00B259;
        color: white;
        border: 1px solid #ccc;
        font-weight: 400!important;
        height: 49px;
        padding: 10px 16px;
        border-radius: 6px;
        font-size: 20px;
        margin-bottom: 12px;
    }
    #main-login-button {
        border: 1px solid transparent;
        font-size: 14px;
        padding: 6px 12px;
        height: 32px;
        min-width: 61px!important;
        line-height: 1.25;
        border-radius: 3px!important;
        transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
        margin-top: -2px;
    }
    #signup-button:focus, #main-login-button:focus {
        box-shadow: inset 0 1px 1px rgba(0,0,0,0.075),0 0 8px rgba(102,175,233,0.6);
    }
    .icon-password-show-v2, #signup-header-container {
        display: none;
    }
    .signup-or-log-in .signup-container {
        padding: 0px;
    }
    #signup:before {
        content: "";
        background-image: url('http://images.rbxcdn.com/f76c76889f4b167ca1f27edc27eb8146.png');
        background-size: contain;
        display: block;
        width: 457px;
        height: 115px;
        opacity: .99;
        z-index: 10000;
        position: absolute;
        margin-left: -472px;
        margin-top: 95px;
    }
    #signup:after {
        content: "You Make the Game ";
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        font-weight: 300;
        text-align: center;
        color: white;
        font-size: 35px;
        background-color: rgba(0,0,0,0.7);
        padding: 18px 25px;
        display: block;
        position: absolute;
        margin-left: -406px;
        margin-top: -189px;
    }
    .signup-header:after {
        content: " â„¢ ";
        position: absolute;
        font-size: 15px;
        color: white;
        margin-left: -379px;
    margin-top: 195px;
    z-index: 100;
    }
    #action-bar-container {
        height: 66px;
        background-color: rgba(0, 0, 0, 0.35);
    }
    #app-stores:before {
        content: "Game: ROBLOX Point";
        color: white;
        text-align: left;
        line-height: 1;
        font-size: 28px;
        font-weight: 300;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        position: absolute;
        margin-left: -535px;
        margin-top: 123px;
    }
    #app-stores:after {
        content: "Developer: StarMarine614";
        color: white;
        text-align: left;
        line-height: 1;
        font-size: 28px;
        font-weight: 300;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        position: absolute;
        margin-left: -535px;
        margin-top: 152px;
    }
    .input-validation.text-error {
        color: #a94442;
        font-weight: 300!important;
        font-family: "Source Sans Pro",Helvetica,Arial,sans-serif;
        font-size: 16px!important;
        line-height: 1.25;
    }
    #app-stores-devices h4, #app-stores a {
        display: none!important;
    }
    .dark-theme .rbx-select-group .rbx-select option {
        all: revert;
    }
    }
