css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xda-developers.com") {

body
{
	background: #FFFFFF !important;
	color: #000000;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	margin: 0 auto !important;
	padding: 0 10px 10px 10px !important;
	width: inherit !important;
}
textarea, .bginput
{
	background: #FFFFFF !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.navbar
{
	color: #000000 !important;
}
legend
{
color: #000000 !important;
font-weight: bold !important;
}
.navbar a
{
color: #0892D0 !important;
}
span.navbar
{
	margin-left: -2px !important;
}
td.navbar
{
	font-size: 18px !important;
}
td.navbar a, div[style="margin: 5px 20px 20px;"] table img, #threadslist td.thead a img, td.tcat a img, td.alt2 img.inlineimg, #tag_edit_link, a[href="tags.php"], #tag_list_cell, table[style="margin-bottom: 3px;"] td.smallfont a,  td.thead div.normal a img, .tborder[cellpadding="6"] td.alt1 div.smallfont img.inlineimg, td[id*="td_threadtitle"] img, div[id*="postmenu"] img, td.thead span[style="float: right;"]
{
	display: none !important;
}
table#threadslist td.thead
{
padding: 10px 0 !important;
}
td.alt1[id*=post] div
{
padding-left: 10px !important;
padding-right: 10px !important;
}
body:first-child div:first-child div:first-child div:first-child table:first-child
{
	margin-bottom: 0px !important;
}
body div:first-child div:first-child div:first-child table:first-child td.navbar img, div[style="padding: 6px 0px 0px;"], table[cellspacing="6"][cellpadding="0"][width="100%"] td[nowrap="nowrap"][valign="top"] img
{
	display: none !important;
}
div[align="right"][style="margin-top: 10px;"] a img
{
display: none;
}
div[align="right"][style="margin-top: 10px;"]
{
border-top: #EAEAEA 1px solid !important;
padding: 10px 0 10px 0 !important;
}
div[align="right"][style="margin-top: 10px;"] a
{
border-left: 1px solid #EAEAEA !important;
padding: 0 1px 0 5px;
}
div[align="right"][style="margin-top: 10px;"] a:first-child
{
border-left: none !important;
}
div[align="right"][style="margin-top: 10px;"] a:after
{
content: "Reply w/ Quote";
}
div[align="right"][style="margin-top: 10px;"] a[onclick="return false"]:after
{
content: "Multi-Quote";
}
div[align="right"][style="margin-top: 10px;"] a[id*="qr"]:after
{
content: "Quick Reply";
}
div.page div[style="padding: 0px 25px;"] hr
{
display: none;
}
table.tborder tbody tr td.alt1 table tbody tr td a img
{
	display: none !important;
}
.alt2 form
{
padding: 10px 10px !important;
}
#navbar_username
{
margin-bottom: 2px !important;
}
.alt2 form table input
{
	margin: 0 5px !important;
}
div[style="padding: 0px 25px;"] div[clear="all"], div[style="padding: 0px 25px;"] div.smallfont[align="center"]
{
	display: none !important;
}
table.tborder tbody tr td[width="73%"], table.tborder tbody tr td[width="27%"]
{
	display: none !important;
}
div[style="padding: 0px 25px;"] div table.tborder div.smallfont strong, td.thead div.normal[style="float: right;"], td.thead div.normal[style="float: right;"] a
{
color: #000000 !important;
font-size: 18px !important;
}
td.thead
{
background: #FFFFFF !important;
vertical-align: bottom !important;
}
div[style="padding: 0px 25px;"] div table.tborder div.smallfont strong
{
border-bottom: 1px solid #EAEAEA !important;
display: block;
margin: 10px auto 15px auto;
padding-bottom: 5px !important;
}
a:link, a:visited, a:hover, a:active, body_ahover
{
	color: #0892D0 !important;
	text-decoration: none;
}
.page
{
	background: #FFFFFF !important;
	color: #000000;
	overflow: hidden !important;
}
.page div
{
padding: 0 0 !important;
}
table
{
border-spacing: 0 !important;
}
.page a:hover, .page a:active, .page_ahover
{
	color: #000000 !important;
}
.tborder
{
	background: #FFFFFF !important;
	color: #000000;
	border: 0px !important;
}
div.page div div table[cellspacing="6"] tbody tr td
{
padding: 10px !important;
}
div[style="margin: 5px 20px 20px;"] table td, div[style="margin: 5px 20px 20px;"] pre.alt2
{
background: #F6F6F6 !important;
border: 1px solid #EAEAEA !important;
-moz-border-radius:3px 3px 3px 3px;
padding: 10px !important;
}
div[style="margin: 5px 20px 20px;"] div[style="margin: 5px 20px 20px;"] pre.alt2
{
background: #EEEEEE !important;
border: 1px solid #DDDDDD !important;
-moz-border-radius:3px 3px 3px 3px;
padding: 10px !important;
}
#threadslist tbody:first-child tr:first-child td
.tborder table
{
	width: inherit !important;
}
.tcat
{
	background: #FFFFFF !important;
	color: #000000;
	font: bold 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.tcat a:hover, .tcat a:active, .tcat_ahover
{
	color: #000000 !important;
	text-decoration: none;
}
.thead
{
	color: #AAAAAA !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.thead a:link, .thead_alink, .thead a:link strong, .thead a:visited strong
{
	color: #0892D0 !important;
}
.thead a:visited, .thead_avisited
{
	color: #0892D0 !important;
}
.thead a:hover, .thead a:active, .thead_ahover
{
	color: #0892D0 !important;
}
.tfoot
{
	background: #F6F6F6 !important;
	border: 1px solid #EAEAEA !important;
	color: #000000;
	-moz-border-radius:3px 3px 3px 3px;
	padding-right: 10px !important;
}
.tfoot a:link, .tfoot_alink
{
	color: #0892D0 !important;
}
.tfoot a:visited, .tfoot_avisited
{
	color: #0892D0 !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover
{
color: #0892D0 !important;
text-decoration: underline;
}
.alt1, .alt1Active
{
	background: #FFFFFF !important;
	color: #000000;
}
.alt2, .alt2Active
{
	background: #F6F6F6 !important;
	color: #000000;
}
div#posts td.alt2[style="padding: 0px;"]
{
background: #FFFFFF;
border: 1px solid #EAEAEA !important;
margin-bottom: 10px !important;
-moz-border-radius:3px 3px 3px 3px;
}
td[id*=post]
{
display: block;
margin-top: 10px !important;
}
td.alt2[style="padding: 0px;"] table tbody tr td
{
background: transparent !important;
}
hr
{
color: #F6F6F6 !important;
}
.inlinemod
{
	background: #909090 !important;
	color: #000000;
}
.wysiwyg
{
	background: #909090 !important;
	color: #000000;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	margin: 5px 10px 10px 10px;
	padding: 0px;
}
.button
{
	background: #AAAAAA !important;
	color: #000000;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
select
{
	background: #FFFFFF !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.fjsel
{
	background: #DDDDDD !important;
	color: #000000;
}
.fjdpth0
{
	background: #F6F6F6 !important;
	color: #000000;
}
.panelsurround
{
	background: #FFFFFF !important;
}
.panel
{
background: #F6F6F6 !important;
padding: 10px !important;
border: 1px solid #EAEAEA !important;
-moz-border-radius:3px 3px 3px 3px;
}
.panel div:first-child
{
margin: 10px !important;
max-width: 100% !important;
}
#vB_Editor_QR_controls div.imagebutton
{
background: #F6F6F6 !important;
}
#vB_Editor_QR
{
background: transparent !important;
border: 0px !important;
}
.controlbar[style="padding-right: 8px;"], textarea#vB_Editor_QR_textarea
{
background-color: #EAEAEA !important;
border: 1px solid #EAEAEA !important;
-moz-border-radius:3px 3px 3px 3px;
}
fieldset[style="margin: 3px 0px 0px;"]
{
border: none !important;
margin: 5px 0 0 0 !important;
padding: 0 !important;
}
fieldset[style="margin: 3px 0px 0px;"] div[style="float: right;"], lable#qr_quickreply
{
margin: 0px !important;
}
fieldset.fieldset[style="margin: 3px 0px 0px;"] legend
{
display: none !important;
}
.vbmenu_control
{
	background: #F6F6F6 !important;
	color: #AAAAAA !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	padding: 8px 6px 8px 6px !important;
	white-space: nowrap;
}
.vbmenu_popup
{
border: none !important;
}
.vbmenu_popup table
{
border: 1px solid #DDDDDD !important;
background: #EEEEEE;
margin-top: 2px;
-moz-border-radius:3px 3px 3px 3px;
}
.vbmenu_popup, .vbmenu_popup form
{
background: transparent !important;
}
div[style="padding: 1px; border-top-width: 0px;"] tr .vbmenu_control:first-child
{
border-left: 1px solid #EAEAEA !important;
-moz-border-radius:3px 0 0 3px;
}
div[style="padding: 1px; border-top-width: 0px;"]
{
border-right: 1px solid #EAEAEA !important;
-moz-border-radius:0 3px 3px 0;
}
div[style="padding: 1px; border-top-width: 0px;"] table tr td
{
border-bottom: 1px solid #EAEAEA !important;
border-top: 1px solid #EAEAEA !important;
}
.pagenav table .vbmenu_control, .pagenav table .alt2, .vbmenu_control[id*=thread], .vbmenu_control[id*=display], div.page div[style="padding: 0px 25px;"] table tbody tr td.alt2[nowrap="nowrap"], #forumtools, #forumsearch
{
background: #FFFFFF !important;
}
.vbmenu_option
{
	background: #F6F6F6 !important;
	color: #000000;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
table[cellspacing="1"][cellpadding="4"][border="0"] tr td
{
background-color: transparent !important;
color: #000000 !important;
}
table[cellspacing="1"][cellpadding="4"][border="0"] tr:first-child td
{
-moz-border-radius:3px 3px 0 0;
}
.vbmenu_control a:active, .vbmenu_control_ahover, .vbmenu_control a:visited
{
        color: #0892D0 !important;
    text-decoration: none !important;
}
.vbmenu_control a:hover
{
color: #000000 !important;
text-decoration: none !important;
}

.vbmenu_hilite
{
	background: #909090 !important;
	color: #330000;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
table[style="margin-top: -3px;"]
{
margin: 5px auto 10px auto !important;
}
table[style="margin-top: -3px;"] td.smallfont a img
{
display: none;
}
table[style="margin-top: -3px;"] td.smallfont a:after
{
content: "Post Reply";
font-size: 18px;
font-weight: bold;
}
table.tborder[width="210"]
{
display: none;
}
form[action="index.php"]
{
margin-top: 10px !important;
}
form[action="index.php"] div, form[action="index.php"] br, div[align="center"] div.page div[style="padding: 0px 25px;"] br
{
display: none !important;
}
div#posts div[align="center"] div.page div[style="padding: 0px 25px;"] br
{
display: inline !important;
}
form[action="index.php"] table div
{
display: inline !important;
}

td[id*=td_post]
{
padding: 0 !important;
}


*
{
border-color: #DDDDDD !important;
}

/* *****alt3 alternating color 3 made by flar ***** */
.alt3 {background-color:#AAAAAA !important;}


}
