@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("muusikoiden.net") {


/** Muusikoiden.net Dynamic CSS - Generated 07.03.2010 10:42:16 **/

* {
    font-family: Verdana, Arial, Helvetica, sans;
}

PRE {
    font-family: Courier New, Monospace;
    font-size: 9pt;
}

BODY {
    padding: 0;
    margin: 0;
    background: #000000 !important;
    color: #cccccc !important;
}

H1 {
    color: #889988 !important;
    text-shadow: #556655 1px 1px !important;
    font-size: 9pt; 
    margin-top: 0px;
    margin-bottom: 4px;
    padding-top: 0px;
    text-transform: uppercase;
    font-weight: bold;
}

/** Basic Links **/

A {
    color: #acacac !important;
    text-decoration: none;
    border-bottom: 1px dotted #5c5c5c !important;
}

A:active {
    color: #acacac !important;
    text-decoration: none;
    border-bottom: 1px solid #686868 !important;
}

A:visited {
    color: #acacac !important;
    text-decoration: none;
    border-bottom: 1px dotted #5c5c5c !important;
}

A:hover {
    color: #cecece !important;
    text-decoration: none;
    border-bottom: 1px solid #686868 !important;
}

/** Disabled "Links" **/

.disabled_link {
    color: #676767 !important;
    text-decoration: none;
    border-bottom: 1px dotted #676767 !important;
}

/** No Hover Links **/

A.nohover, A.nohover:hover {
    color: #acacac !important;
    border-bottom: 0;
}

A.h1, A.h1:active, A.h1:visited, A.h1:hover {
    border-bottom: 0;
    color: #ededed !important;
}

/** Images **/

IMG.border {
    border: 1px solid #5c5c5c !important;
}

/** Lighter Text And Links **/

.light, .light A, .light A:visited, A.light, A.light:visited {
    color: #bdbdbd !important;
}

.light A, .light A:visited, A.light, A.light:visited {
    border-bottom: 1px dotted #bdbdbd !important;
}

.light A:hover, .light A:active, A.light:hover, A.light:active {
    color: #bdbdbd !important;
    border-bottom: 1px solid #bdbdbd !important;
}

/** Warnings Text And Links **/

.error, .error A, A.error {
    color: #cc4433 !important;
}

.error A, A.error, .error A:visited, A.error:visited {
    border-bottom: 1px dotted #cc4433 !important;
}

.error A:hover, A.error:hover, .error A:active, A.error:active {
    border-bottom: 1px solid #cc4433 !important;
}

/** Ok Texts **/

.green, .green A, A.green {
    color: #009900;
}

.green A, A.green, .green A:visited, A.green:visited {
    border-bottom: 1px dotted #009900;
}

.green A:hover, A.green:hover, .green A:active, A.green:active {
    border-bottom: 1px solid #009900;
}

/** Text Colored Links **/

A.textlink, A.textlink:visited {
    color: #b8b8b8;
    border-bottom: 1px dotted #b8b8b8;
}

A.textlink:hover, A.textlink:active {
    border-bottom: 1px solid #b8b8b8;
}

/** Link Colored Texts **/

.linkcolor {
    color: #5c5c5c;
}

/** Quote **/

.quote, .quote A, A.quote {
    color: #555555;
    font-style: italic;
}

.quote A, A.quote, .quote A:visited, A.quote:visited {
    border-bottom: 1px dotted #555555;
}

.quote A:hover, A.quote:hover, .quote A:active, A.quote:active {
    border-bottom: 1px solid #555555;
}

/** Important Objects **/

TABLE.important, TD.important {
    background: #cc4433 !important;
}

A.important {
    color: #121212;
    border-bottom: 1px dotted #121212;
}

A.important:hover {
    border-bottom: 1px solid #121212;
}

.important {
    color: #cc4433 !important;
}

DIV.important {
    border: 1px solid #cc4433 !important;
    padding: 2px;
}

/** Highlighted Text **/

.highlight {
    color: #b8b8b8;
    background: #ffff66;
}

/** Hidden Text */

.hidden {
    color: #121212;
}

/** Sidemenu Components **/

A.sidemenu, A:visited.sidemenu {
    font-family: Arial, Helvetica, sans;
    font-size: 9.75pt;
    font-weight: bold;
    color: #e6e6e6;
    text-transform: uppercase;
    text-shadow: #404040 1px 1px;
    display: block;
    border: 0px;
    padding: 1px 1px 1px 5px;
    margin: 1px 1px 0px 1px;
}

A:hover.sidemenu {
    color: #e6e6e6;
    background: #303030;
    border: 0px;
}

A:active.sidemenu {
    background: #070707;
}

HR.sidemenu {
    width: 140px;
    height: 0px;
    margin: 3px 5px 3px 5px;
    background: #0b0b0b;
    color: #0b0b0b;
    border-top: 1px solid #070707;
    border-bottom: 1px solid #303030;
    border-left: 0px;
    border-right: 0px;
}

A.nebula {
    font-size: 7pt; 
    color: #e6e6e6;
    text-shadow: #404040 1px 1px;
    border-bottom: none;
}

A.nebula:hover {
    text-decoration: underline;
}

/** Column Separators **/

TD.vert_sep {
    border-left: 1px dotted #3d3d3d;
}

HR.hort_sep {
    margin: 10px 0px 10px 0px;
    padding: 0;
    border-top: 1px dotted #3d3d3d;
    color: #121212; 
    background-color: #121212; 
    height: 1px;
    width: 100%;
}

/** Quickmenu Components **/

.quickmenu {
    font-size: 6.5pt;
    font-weight: bold;
    color: #ff00ff;
    text-transform: uppercase;
    border-bottom: 0px;
    border-top: 3px solid #121212;
}

.quickmenu A, .quickmenu A:visited {
    color: #ff00ff;
    border-bottom: 0px;
}

.quickmenu A:hover, .quickmenu A:active {
    border-bottom: 1px solid #ffffff;
}

/** Default Font Sizes **/

BODY, P, TD, TH, BLOCKQUOTE {
    font-size: 7.75pt;
    line-height: 1.35em; 
}

SMALL {
    font-size: 7pt; 
    line-height: 1.35em;
}

/** Table Settings **/

TH {
    text-align: left;
    color: #5c5c5c;
    font-weight: bold;
    background: #101010 !important;
}

TR.bg, TD.bg {
    background: #000000 !important;
}

TR.bg2, TD.bg2 {
    background: #101010 !important;
}

TR.bg3, TD.bg3 {
    background: #101010 !important;
}

/** Keskustelu **/

TABLE.keskustelu {
    border-top: none;
    border-bottom: 1px solid #3f3f3f;
    border-left: 1px solid #3f3f3f;
    border-right: none;
    color: #888888 !important;
    padding: 0px;
    margin: 0px;
    border-spacing: 0px;
    background: #000000 !important;
}

.keskustelu TH {
    color: #aaaaaa !important;
    -moz-appearance: -moz-win-media-toolbox;
    font-weight: normal;
    background: #444444 !important;
    padding: 4px;
}

.keskustelu TH A, .keskustelu TH A:active, .keskustelu TH A:visited {
    color: #c3c3c3 !important;
    font-weight: bold;
    border-bottom: 1px dotted #c3c3c3 !important;
}

.keskustelu TH A:hover {
    border-bottom: 1px solid #a1a1a1;
}

.keskustelu TH B.linkcolor {
    color: #a1a1a1;
}

.keskustelu TH B.selected_page {
    color: #cfcfcf !important;
    background: none !important;
    padding: 1px 2px 1px 2px;
}

.keskustelu TD {
    border-top: 1px solid #3f3f3f; 
    border-bottom: none;
    border-right: 1px solid #3f3f3f;
    border-left: none;
    border-spacing: 0px;
    padding: 3px 6px 3px 6px;
}

.keskustelu SMALL {
    font-weight: normal;
}

FONT.msg {
    font-size: 9pt;
    line-height: 1.30em;
}

FONT.tab {
    font-family: "Courier New", Monospace, fixed;
}

.keskustelu_nick, A.keskustelu_nick:visited, .keskustelu_nick_ban {
    font-size: 9pt;
    font-weight: bold;
    color: #666666 !important;
}

A.keskustelu_nick:hover {
    color: #333333 !important;
    border-bottom: 1px solid #b8b8b8;
}

A.keskustelu_nick_ban {
    color: #b8b8b8;
    border-bottom: 1px dotted #ff0000;
}

A.keskustelu_nick_ban:hover {
    color: #ff0000;
    border-bottom: 1px solid #ff0000;
}

.keskustelu_ip {
    color: #676767;
    font-size: 6pt;
}

A.conference_link {
    font-weight: bold;
    font-size: 9pt;
}

A.thread_link, FONT.thread_link {
    font-size: 9pt;
}

.noborder TABLE, .noborder TD {
    border: none;
    padding: 0px;
    color: #808080 !important;
}

/** Moderaattorit **/

FONT.warning_count {
    padding: 1px;
    border: 1px solid #676767;
    color: #676767;
}

A.warning_count, A.warning_count:hover {
    padding: 1px;
    border: 1px solid #676767;
    color: #ff0000;
}

/** Misc **/

P {
    margin-top: 0.90em;
    margin-bottom: 0.90em;
}

.message {
    margin-top: 20px;
    margin-bottom: 20px;
    margin-left: 20px;
    margin-right: 20px;
}

.operaatio {
    margin-top: 15px;
    margin-bottom: 15px;
    margin-left: 15px;
    margin-right: 15px;
}

.sig, .sig A {
    color: #676767; 
}

.sig A, .sig A:visited {
    border-bottom: 1px dotted #676767;
}

.sig A:hover {
    border-bottom: 1px solid #676767;
}

.tabtag {
    margin-top: 0px;
    font-family: "Courier New", monospace; 
    font-size: 8pt;
    white-space: pre;
}

/** Form Components **/

FORM {
    margin: 0;
}

INPUT, SELECT, TEXTAREA {
    font-size: 7.50pt;
    background-color: #121212;
    color: #b8b8b8;
    border: 1px solid #282828;
}

INPUT.checkbox {
    padding: 0;
    margin: 0;
}

INPUT.submit, A.button, A.button:visited {
    font-size: 7.50pt;
    color: #e6e6e6;
    background: #0b0b0b;
    border-top: solid 2px #303030;
    border-left: solid 2px #303030;
    border-right: solid 2px #070707;
    border-bottom: solid 2px #070707;
}

INPUT.submit {
    padding: 1px 4px 1px 4px;
}

A.button, A.button:visited {
    padding: 2px 7px 2px 7px;
}

INPUT.submit:hover, A.button:hover {
    text-decoration: underline;
    color: #e6e6e6;
}

INPUT.submit:active, A.button:active {
    border-bottom: solid 2px #303030;
    border-right: solid 2px #303030;
    border-left: solid 2px #070707;
    border-top: solid 2px #070707;
}

LABEL {
    cursor: default;
}

/** HR **/

HR {
    margin: 0;
    padding: 0;
    border: 0;
    color: #b8b8b8; 
    background-color: #b8b8b8; 
    height: 1px;
    width: 100%;
}



/****************/
/** Quick Menu **/
/****************/

DIV.quickmenu_popup {
    background: #ffffff !important;
    text-align: right;
    padding: 4px 8px 4px 8px;
    border-top: 2px solid #303030 !important;
    border-left: 2px solid #303030 !important;
    border-bottom: 2px solid #070707 !important;
    border-right: 2px solid #070707 !important;

    position: absolute;
    top: 107px;
    right: 5px;
    z-index: 1;
}

DIV.quickmenu_popup A, DIV.quickmenu_popup A:visited, DIV.quickmenu_popup A:active {
    display: block;
    color: #343434 !important;
    text-shadow: #cccccc 1px 1px !important; 
    border: 0px;
    padding: 1px;
}

DIV.quickmenu_popup A:hover {
    background: #343434 !important;
    color: #eeeeee !important;
    border: 0px;
    padding: 1px;
}

/** Tiny Letter Box **/

SMALL.tiny_letter_box {
    font-size: 5pt;
    font-weight: bold;
    color: #676767;
    border: 1px solid #676767;
    padding: 1px;
    margin: 0px 0px 0px 1px;
}

/** End **/
}
