/* ==UserStyle==
@name           VIP Servers/This game is uncopylocked
@namespace      idk
@version        1.0.0
@description    code made by Mystery Channel and xammand: https://userstyles.org/users/1185261 (Mystery Channel)  https://userstyles.world/user/xammand (xammand), respect to all.
@author         Me
==/UserStyle== */
@-moz-document url-prefix("https://web.roblox.com/games/"), url-prefix("https://www.roblox.com/games/") {
/*Recommended Games*/

h3[ng-bind="'HeadingGameBadges' | translate"]:before {
    content: "GAME ";
    cursor: text;
}

.stack-list {
    margin: 0 0 20px;
}

.stack.badge-container.ng-scope .stack-row {
    background-color: white;
    margin: 0 0 6px;
    padding: 5px 12px;
    border-radius: 3px;
    box-shadow: 0 1px 3px rgb(150 150 150 / 74);
}

.stack.badge-container.ng-scope .stack-row .thumbnail-2d-container {
    width: 150px;
    height: 150px;
    background-color: white;
}

div[ng-bind="badge.name"] {
    font-weight: 600 !important;
}

p[ng-bind="badge.description"] {
    font-weight: 300;
    font-size: 16px;
    margin: 12px 0 !important;
}

.game-badges-list .badge-row .badge-stats-container li .text-label, game-badges-list .badge-row .badge-stats-container li .text-label, .game-badges-list .badge-row .badge-stats-container li .badge-stats-info, game-badges-list .badge-row .badge-stats-container li .badge-stats-info {
    font-size: 16px !important;
    color: #191919 !important;
    font-weight: 300 !important;
}

.game-badges-list .badge-row .badge-stats-container li .text-label, game-badges-list .badge-row .badge-stats-container li .text-label:after {
    content: ":";
}

.light-theme .btn-control-sm {
    background-color: white;
    border-color: #b8b8b8;
    color: #757575;
    height: auto;
    transition: all,0.2s,ease-in-out;
    padding: 9px 24px;
    font-size: 1.125em;
    line-height: 100%;
    border-radius: 3px;
}

.light-theme .btn-control-sm:hover {
    background-color: white;
    box-shadow: 0 1px 3px rgb(150 150 150 / 74);
    border-color: #b8b8b8;
    color: #757575;
}

.light-theme .btn-control-sm:focus {
    background-color: white;
    box-shadow: none;
    border-color: #b8b8b8;
    color: #757575;
}
.light-theme .rbx-private-server-create-button {
    background-color: #00a2ff;
    border-color: #00a2ff;
    color: white;
    height: auto;
    transition: all,0.2s,ease-in-out;
    padding: 9px 24px;
    font-size: 0;
    line-height: 100%;
    border-radius: 3px;
}

.light-theme .rbx-private-server-create-button:before {
    content: "Create VIP Server";
    font-size: 18px;
    line-height: 100%;
}

.light-theme .rbx-private-server-create-button:hover {
    background-color: #32b5ff;
    box-shadow: 0 1px 3px rgb(150 150 150 / 74);
    border-color: #32b5ff;
}

.light-theme .rbx-private-server-create-button:focus {
    background-color: #32b5ff;
    box-shadow: none;
    border-color: #32b5ff;
}

div[id="private-server-container-about-tab"] .section-content.remove-panel {
    height: 0px;
    margin: 0;
}

.game-main-content.follow-button-enabled .favorite-follow-vote-share .voting-panel {
    width: 112px !important;
    bottom: -31px !important;
    float: left;
}
light-theme .tooltip.bottom .tooltip-arrow {
    border-bottom-color: #757575 !important;
}
.light-theme .rbx-private-server-create-button::before {
    content: "Create VIP Server" !important;
    font-size: 24px !important;
    line-height: 15px !important;
}
.game-stat-footer:before {
    content: "This game is copylocked";
    display: table;
    float: left;
    font-weight: 400;
    font-size: 15px;
    color: #b8b8b8;
}
.grid-item-container.game-card-container:hover .light-theme .game-card-info {
    border-bottom: 1px solid #E3E3E3 !important;
}
.light-theme .game-card-info {
    position: absolute;
    width: 114px !important;
    margin: 0px 3px !important;
    height: 45px !important;
    top: 151px !important;
}
.light-theme .game-card-container .game-card-name {
    padding: 0 6px;
    margin: 6px 0;
    width: 120px !important;
    margin-top: 2px!important;
    font-weight: 300;
}
.game-card-name, .game-name-title {
    height: 20px !important;
}
.game-name-title {
    overflow: hidden;
    position: relative;
    line-height: 22px !important;
    max-height: 44px !important;
}
.grid-item-container.game-card-container:hover {
    background-color: #fff;
    height: 220px !important;
}
.grid-item-container.game-card-container {
    width: 120px !important;
    height: 196px !important;
    background-color: #fff;
}
.light-theme .game-card-info {
    border-bottom: 1px solid #E3E3E3 !important;
}
.light-theme .game-card-info::before {
    content: "By: ";
    margin-left: -16px;
    position: relative;
    font-size: 12px;
    font-weight: 400;
    color: #b8b8b8;
    top: 42px;
    left: 23px;
    display: none;
}
.light-theme .game-card-info::after {
    content: "ROBLOX";
    position: relative;
    font-size: 12px;
    font-weight: 400;
    color: #00a2ff;
    top: 2px;
    left: 26px;
    display: none;
}
.grid-item-container.game-card-container:hover .game-card-info::before {
    display: inline-block !important;
}
.grid-item-container.game-card-container:hover .game-card-info::after {
    display: inline-block !important;
}
.grid-item-container.game-card-container:hover .info-label {
    margin-top: -2px !important;
}
.light-theme .game-card-container:hover .game-card-info .info-label.icon-votes-gray {
    margin-top: 0px !important;
    margin-left: 0.5px !important;
}
.grid-item-container.game-card-container:hover span.info-label.vote-percentage-label {
    margin-top: 1px !important;
}
.light-theme .game-card-info .info-label {
    color: hsla(0,0%,0%,.7);
}
.light-theme .game-card-info .info-label {
    padding-right: 0;
    margin-right: -2px;
}
div.game-card-info, span.playing-counts-label:after {
    content: " Playing";
}
.light-theme .game-card-container .game-card-name {
    padding: 0 1px;
    margin: 6px 0;
    width: 120px;
    margin-top: 2px!important;
    font-weight: 300;
}
.light-theme .game-card-info .info-label.icon-votes-gray {
    background-image: url(https://raw.githubusercontent.com/anthony1x6000/ROBLOX2016stylus/cd9b642ccf4ef8bfd605a035bb2cc53db92f4e24/images/archiveofimages/153fd3209692232cd8087cad74a669b0-games.svg) !important;
    background-position: 0 -351px !important;
}
.grid-item-container.game-card-container:hover .info-label.icon-votes-green {
    background-position: -16px -351px !important;
}
.light-theme .game-card-info .info-label {
    padding-right: 0;
    margin-right: -2px;
}
.game-card-info .icon-playing-counts-gray {
    visibility: hidden;
}
.game-card-info .playing-counts-label {
    position: relative;
    display: block!important;
    bottom: 25px;
    right: 2px;
}
.game-card-link .game-card-info .vote-percentage-label {
    padding: 0 12px 0 3px;
}
.info-label.vote-percentage-label {
    top: 12px !important;
    position: relative !important;
}
div#recommended-games-container {
    height: 240px !important;
}
.light-theme .game-card-container .game-card-thumb-container {
    background-color: rgba(0,0,0,.1);
    border-bottom-left-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
    border-top-left-radius: 3px !important;
    border-top-right-radius: 3px !important;
}
.game-card-link {
    height: 100%;
    border-radius: 3px;
}
.light-theme .game-card-container {
    box-shadow: 0 1px 4px 0 #19191966;
    border-radius: 3px !important;
}
.dark-theme .game-card-container, .light-theme .game-card-container:hover {
-webkit-transition: box-shadow 400ms ease !important;
    -o-transition: box-shadow 400ms ease !important;
    transition: box-shadow 400ms ease !important;
    box-shadow: 0 1px 6px 0 #191919bf;
}
.game-carousel {
    height: 223px;
    justify-content: center;
    width: 100% !important;
}
}
