css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('evangelische-kirchengemeinde-alpen.de') {

/***************
 * Allgemeines *
 ***************/

body {
   text-align: left !important;
}

h1 {
   color: #600066 !important;
}

h2 {
   color: #ffffff !important;
   background-color: #800088 !important;
   text-shadow: #000000 1px 1px 1px;
}

h3 {
   color: #800088 !important;
}

a {
   color: #600066 !important;
}

/******************************************
 * Seitenkopf inkl. MinimenÃ¼ und Suchfeld *
 ******************************************/
#header {
   background: url("http://www.evangelische-kirchengemeinde-alpen.de/fileadmin/img/sliding_door/header_new.gif") repeat-x #800088 !important;
   height: 115px !important;
}

#topnav a {
   color: #eeeeee !important;
}

#header div.tx-macinasearchbox-pi1 {
   bottom: -22px !important;
}

/*************
 * HauptmenÃ¼ *
 *************/
#nav_main {
   background-color: #800088 !important;
   text-shadow: #ffffff 1px 1px 1px;
}

#nav_main a {
   background: url("http://www.evangelische-kirchengemeinde-alpen.de/fileadmin/img/sliding_door/left_both_new.gif") no-repeat scroll 0 -150px transparent !important;
}

#nav_main a:hover {
   background-position: 0 0 !important;
   background: url("http://www.evangelische-kirchengemeinde-alpen.de/fileadmin/img/sliding_door/left_both_new.gif") no-repeat transparent !important;
}

#nav_main a span {
   background: url("http://www.evangelische-kirchengemeinde-alpen.de/fileadmin/img/sliding_door/right_both_new.gif") no-repeat scroll 100% -150px transparent !important;
}

#nav_main a:hover span {
   background-position: 100% 0 !important;
}

#nav_main #current a {
   background: url("http://www.evangelische-kirchengemeinde-alpen.de/fileadmin/img/sliding_door/left_both_new.gif") no-repeat transparent !important;
   background-position: 0 0;
}

#nav_main #current a span {
   background-position: 100% 0 !important;
}

/******************************
 * Leiste unter dem HauptmenÃ¼ *
 ******************************/
#teaser {
   background: url("http://www.evangelische-kirchengemeinde-alpen.de/fileadmin/img/sliding_door/teaser_new.gif") repeat-x #cc88cc !important;
   color: #ffffff !important;
}

.rootline a {
   color: #600066 !important;
}

.selectFontSize {
   display: none !important;
}

/************* 
 * UntermenÃ¼ *
 *************/
#submenu li#title {
   color: #400044 !important;
   border-bottom:4px solid #800088 !important;
}

#submenu li strong {
   background-color: #cc88cc !important;
   color: #ffffff !important;
   text-shadow: #000000 1px 1px 1px;
}

#submenu li a {
   color: #400044 !important;
}

#submenu li a:focus, #submenu li a:hover, #submenu li a:active {
   background-color: #fdddf8 !important;
   color: #400044 !important;
}


/****************************************************
 * Text in Tempora/Gottesdienste auf der Startseite *
 ****************************************************/
.tx-mininews-pi1-listrowField-teaser a {
   color: #000000 !important;
}

/********************
 * Bildunterschrift *
 ********************/
dd.csc-textpic-caption {
   background-color: #cc88cc !important;
   text-align: center !important;
   color: #ffffff !important;
   font-style: normal !important;
   text-shadow: #000000 1px 1px 1px;
}

/************
 * Tabellen *
 ************/

.contenttable th {
   background-color: #800088 !important;
   text-shadow: #000000 1px 1px 1px;
}

}
