/*
 * Dumppi - Darker Version
 *
 * Author      : Veli-Pekka Parhiala
 * Updated     : March 8th, 2010
 * Description : Modifies the colors, banner and background
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dumppi.fi") {
html {
background-image:url(http://users.jyu.fi/~veparhia/css/bg.jpg)!important;
background-position:right top!important;
background-color:#05080d!important;
background-repeat: no-repeat;
}

div#container {
border-right:2px solid #15181d!important;
border-left:2px solid #15181d!important;
border-bottom:2px solid #15181d!important;
}

div#header {
background-image: url('http://users.jyu.fi/~veparhia/css/header.jpg') !important;
background-repeat: no-repeat!important;
background-position: 0px 5px!important;
background-color:#05080d!important;
}
div#contentborder {
border-left:1px solid #05080d!important;
border-right:1px solid #05080d!important;
}
div#content .menus {
background-color:#e8e8e8!important;
-moz-border-radius: 7px;
border-top-width:0px!important;
border-right-width:0px!important;
border-bottom-width:0px!important;
border-left-width:0px!important;
}
}
