css:
@-moz-document regexp("https://.*\\.locals\\.com\\/post\\/\\d+\\/.*") {
#live-chat-block {
    max-width: 100%;
}
.pmessage__content {
    max-width: 100%;
}
.pmessage__replyto {
    background-color: #2a2a2a;
    padding: 10px;
}
.pmessage__content {
    background: #353535;
}
.msg-text {
    color: #ccc;
    font-size: 20px;
}
.pmessage__replyto {
    color: #aaa !important;
    margin-bottom: 10px;
}
.pmessage__replyto span:nth-child(1) {
    /* replyee name */
    color: #32b259;
}
.pmessage--owner .pmessage__content {
    background-color: #404040;
}
.pnotice {
    /* is typing text */
    color: white;
    font-size: 18px;
}
.post {
    display: none !important;
}
.chat-scroll {
    display: block;
    font-size: 20px;
}
#toggle-livechat {
    display: none !important;
}
.pmessage .cm-actions-dropdown.cm-actions-dropdown--dots .cm-actions-dropdown__btn {
    background: #353535;
    color: white;
}
svg {
    filter: invert(80%);
}
label.chat-tip__btn svg {
    filter: brightness(1%);
}
.pmessage:hover {
    background: #1c1c1c;
}
i.icon.i-send {
    filter: invert(20%) sepia(100%) saturate(400%) hue-rotate(90deg) brightness(80%) contrast(100%);
}
.pmessage--creator .pmessage__link, .theme .post--live .item.clickable.active, .question-poll .form-footer .cancel-btn, .pmessage__select {
    color: #32b259 !important;
}
.isLiveChat .pchat #chat-typing {
    background-color: #1f1f1f;
}
.pmessage__link {
    font-size: 20px;
}
.pmessage--creator .pmessage__content {
    background-color: #00360A;
}
.reply-wrap-desk .chatreply {
    border-color: #1c1c1c;
}
.chatreply {
    background-color: #1c1c1c;
}
.chatreply__username, .chatreply__to:nth-child(2) {
    color: #ccc;
}
.chatinput {
    border-color: #111;
    background-color: #111;
}
/** chat mention popout **/
 .textcomplete-dropdown {
    background-color: #353535;
    border-color: #353535;
}
.textcomplete-footer, .textcomplete-item {
    border-top: 2px solid #32b259;
    padding-bottom: 2px;
    padding-top:2px;
    color: #ccc;
}
mark {
    color: white;
    background: none;
    padding-left: 1px;
    padding-right: 1px;
    font-size: 16px;
    font-weight: bold;
}
.pmessage__text .highlight {
    background-color: #237D3E;
    color: #101010;
}
.pmessage__replyto span:nth-child(1) {
    color: #eee;
    padding-left: 2px;
    padding-right: 2px;
}
.content-tip__header a {
    color: #101010 !important;
    font-weight: bold;
}
.pmessage__text {
    font-size: 20px;
}
.replyto--yellow {
    background: #EBA41A;
}
}
