css:
/* Aplica el estilo solo en el dominio deseado */
@-moz-document domain("example.com") {
  /* Define el keyframe "spin" */
  @keyframes spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  /* Agrega el filtro rosado */
  .icon-logo-r,
  .light-theme .icon-logo-r,
  .dark-theme .icon-logo-r {
    animation: spin 4s linear infinite;
    filter: hue-rotate(300deg) saturate(200%);
  }
}
