css:
@-moz-document url-prefix(https://www.edisonformat.com/) {
li, a, div, .wsite-content-title span, strong, font, .wsite-multicol-tr span, #wsite-content h2, #wsite-content .product-long .product-title, #wsite-content .product-large .product-title, #wsite-content .product-small .product-title, .blog-sidebar h2, #wsite-title, .wsite-menu-item, #wsite-content div.paragraph, #wsite-content div.paragraph span, #wsite-content p, #wsite-content .product-block .product-title, #wsite-content .product-description, #wsite-content .wsite-form-field label, #wsite-content .wsite-form-field label, .blog-sidebar div.paragraph, .blog-sidebar p, .blog-sidebar .wsite-form-field label, .blog-sidebar .wsite-form-field label, .wsite-not-footer h2.wsite-content-title a, .wsite-not-footer .paragraph a, .wsite-not-footer blockquote a, #blogTable .blog-sidebar a, #blogTable .blog-comments a, #blogTable .blog-comments-bottom a, #wsite-com-store a, #wsite-com-product-gen a {
  color: white !important;
  transition-duration: 200ms;
}

table {
  font-family: sans-serif;
}

a:hover, a:hover span:not(#wsite-title) {
  color: #3385ff !important;
}

#cards tr:hover, .content-wrap, .wsite-section-bg-image, #cards tr:nth-child(even){
  background-color: #242526;
  background: #242526;
  background-image: none !important;
}

tbody tr:first-child td.cell, .edison-header, .edison-header .nav-wrap, .footer-wrap, .desktop-nav .wsite-menu-wrap, #cards th, .blog-notice-comments-closed  {
  background-color: #18191A !important;
}

td.cell {
  background-color: #313131 !important;
}

