css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://dabr.co.uk), 
               url-prefix(http://dabr.nl), 
               url-prefix(http://twa.sh)
{

@namespace url(http://www.w3.org/1999/xhtml);
body {
color: #202020 !important;
line-height:1.5em !important;
background:#F4F4ED !important;
width:810px !important; 
margin:0 auto !important;
font-size: 14px !important;
font-family:  'Helvetica Neue', Helvetica, 'Droid Sans', Arial, æ–¹æ­£é»‘ä½“ç®€ä½“, Sans-Serif !important;
text-shadow:0 1px 0 rgba(0,0,0,0.04) !important;
}
div.menu {
font-size:12px !important; 
height:20px !important; 
padding: 2px 5px !important; 
-moz-border-radius: 3px !important; 
-webkit-border-radius: 3px !important; 
margin:5px 0 !important; 
background-color:#E6E6E6 !important; 
color:#D2DADA !important;
border:1px solid #D2DADA !important;
-moz-box-shadow:0 1px 2px rgba(0, 0, 0, 0.1) !important;
-webkit-box-shadow:0 1px 2px rgba(0, 0, 0, 0.1) !important;
}
#status {
max-width:798px !important;
font-family: Georgia !important;
height: 50px !important;
border-color: #aaa;
border-radius: 3px !important;
-moz-border-radius: 3px !important;
-webkit-border-radius: 3px !important;
}

input {
width: 200px !important; 
height:30px !important;
font-family: Georgia !important;
color:#666 !important;
}

input[type=text]:focus,input[type=password]:focus,textarea:focus
{
outline:none;
border-color:rgba(82,168,236,.75)!important;
box-shadow:0 0 8px rgba(82,168,236,.5);
-moz-box-shadow:0 0 8px rgba(82,168,236,.5);
-webkit-box-shadow:0 0 8px rgba(82,168,236,.5);
-webkit-transition:border linear .1s,-webkit-box-shadow linear .1s;
}

#remaining {
font-size:18px !important;
font-family: Georgia !important;
color:#ccc !important;
}

table.timeline {
border:1px dashed #D2DADA !important;
-moz-border-radius: 3px !important;
-webkit-border-radius: 3px !important;
margin: 5px 2px !important;
background:#F4F4ED !important;
-moz-box-shadow:0 1px 2px rgba(0, 0, 0, 0.1) !important;
-webkit-box-shadow:0 1px 2px rgba(0, 0, 0, 0.1) !important;
}



tr.odd td{
border-top:1px dashed #D2DADA !important;
padding:8px !important;
background:#F4F4ED !important;
-moz-box-shadow:0 1px 2px rgba(0, 0, 0, 0.1) !important;
-webkit-box-shadow:0 1px 2px rgba(0, 0, 0, 0.1) !important;
}
tr.even td {
border-top:1px dashed #D2DADA !important;
padding:8px !important;
background:#fcfcfc !important;
}
a {
color:#C04E37 !important;
text-decoration:none !important;
}
a:hover {
color:#8C3929 !important;
}
tr td small a {
color:#999 !important;
}
tr td img {
width:48px !important;
height:48px !important;
}
tr td a img {
width:auto !important;
height:auto !important;
}

input[type="file"] {
width:100% !important;
margin:5px 0 !important;
}

input[name="message"] {
width:100% !important;
height:50px !important;
}

input[value="Upload"] {
margin:5px 0 !important;
width:100px !important;
border:2px solid #999 !important;
background:#999 !important;
}

input[name="query"] {
height:16px !important;
width:100% !important;
}

input[value="Search"] {
height:25px !important;
margin:2px !important;
width:100px !important;
}

input[value="Update"] {
-moz-border-radius:4px;
-khtml-border-radius:4px;
-webkit-border-radius:4px;
background:#ddd url("http://s.twimg.com/a/1280770681/images/buttons/bg-btn.gif") repeat-x 0 0;
border-bottom-color:#ccc;
border-color:#ddd;
border-radius:4px;
border-style:solid;
border-width:1px;
color:#333;
cursor:pointer;
margin:2px 0 3px 598px;
overflow:visible;
padding:4px 8px 5px;
text-shadow:1px 1px 0 #fff;
}

input[value="Update"] 
{outline:none;}

input[value="Update"]:hover,input[value="Update"]:focus
{border-color:#999 #999 #888;color:#000;}

input[value="Update"]:active
{background-image:none;text-shadow:none;outline:none;}

#remaining {
margin-left:-250px;
}

#geo {display:none!important;}


}
