css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://dragcave.net") {

.csort2 img{
background: #000000 !important;
}

td, tr, th img{
background: #bfafcc !important;
}

table, td, td{
text-align: center !important;
font-weight: normal !important;
font-size: 13px !important;
border-spacing: 5px !important;
}


INPUT {
color:#333 !important;
font:8pt Trebuchet MS !important;
text-transform: inherit !important;
}

TEXTAREA {
color:#333 !important;
font:8pt Trebuchet MS !important;
text-transform: inherit !important;
}

html,body {
	padding: 0 !important;
	margin: 0 !important;
}
body {
	color: #382D3B !important;
	background: #000000 none repeat-x !important;
	font-size: 9pt !important;
	font-family: arial !important;
	color: #000000 !important;
}
h1,h2 {
	padding: 0 !important;
	margin: 0 !important;
}

b {     
        color:#999999 !important;font:11px arial !important;
        font-style:italic !important;
        font-weight:bold !important;letter-spacing:-.5pt !important;
}

p {
	text-indent: 1.5em !important;
}
a:link, a:active, a:visited {
	color:#807b9a !important;
        font:12px arial !important;
}
a:hover {
color:#bfafcc !important;
        background:#807b9a !important;
}
th,td {
	text-align: center !important;
}
table.vamid td {
	vertical-align: middle !important;
}
table.leftalign,table.leftalign td,table.leftalign th {
	text-align: left !important;
}
img {
	border-width: 0 !important;
}
acronym {
	cursor: help !important;
}
#wrapper {
	width: 900px !important;
	max-width: 800px !important;
	margin: auto !important;
	text-align: justify !important;
}
#top {
	background: url("http://img.photobucket.com/albums/v55/xXJaveYXx/Untitled-1-1.png") no-repeat !important;
	width: 825px !important;
	color: #000 !important;
	height: 160px !important;
}
#middle {
	background: url("http://img.photobucket.com/albums/v55/xXJaveYXx/Untitled-2-1.png") repeat-y !important;
	padding: 0 60px !important;
	color: #000 !important;
	min-height: 100px !important;
	width: 705px !important;

}
#bottom {
	background: url("http://img.photobucket.com/albums/v55/xXJaveYXx/Untitled-3-1.png") no-repeat !important;
	color: #000 !important;
	width: 825px !important;
	height: 110px !important;
}
#logo {
	display: block !important;
	margin: 0 auto !important;
	width: 562px !important;
	height: 127px !important;
	display:none !important;

}
#menu {
	text-align: center !important;
	margin: 0 auto !important;
}
.cave,.scroll,.login,.register,.shop,.forum,.logout {
	margin: 0 5px !important;
	background: transparent url("http://img.photobucket.com/albums/v55/xXJaveYXx/Untitled-4-2.png") no-repeat !important;
	height: 50px !important;

}
.cave {
	width: 80px !important;
}
.scroll {
	width: 126px !important;
	background-position: -81px center !important;
}
.login {
	width: 98px !important;
	background-position: -212px center !important;
}
.register {
	width: 155px !important;
	background-position: -316px center !important;
}
.shop {
	width: 82px !important;
	background-position: -362px center !important;
	display:none !important;
}
.forum {
	width: 112px !important;
	background-position: -470px center !important;
}
.logout {
	width: 126px !important;
	background-position: -586px center !important;
}
#prefooter {
	margin-left: -11.5px !important;
	height: 100px !important;
	overflow: hidden !important;
	width: 728px !important;
	margin-right: -20px;
}
#footer {
	font-size: 0.8em !important;
	text-align: center !important;
	margin: 0 auto;
}
}
