/* i really want this to be global */
@-moz-document url-prefix(https://spot.upi.edu/mhs){
.btn-custom,.btn-custom:hover{color:#fff;border:1px solid #eb19ff}.preloader{width:100%;height:100%;top:0;position:fixed;z-index:99999;background:#121212}.preloader .cssload-speeding-wheel{position:absolute;top:calc(50% - 3.5px);left:calc(50% - 3.5px)}body{background:#fff}.logo i{color:#fff}#side-menu ul>li>a:hover,.bg-title .breadcrumb .active,.mini-sidebar .sidebar-nav #side-menu>li:hover>a i,.sidebar-nav ul#side-menu li a.active i{color:#eb19ff}#side-menu li a{color:#8d9498}.sidebar-nav ul#side-menu li a.active{color:#2b2b2b}#side-menu ul>li>a.active{color:#2b2b2b;font-weight:500}.fix-sidebar .top-left-part{background:#d93c17}.bg-theme{background-color:#eb19ff!important}.btn-custom,.chat-list .odd .chat-text{background:#eb19ff}.btn-custom:hover{background:#eb19ff;opacity:.8}.customtab li.active a,.customtab li.active a:focus,.customtab li.active a:hover{border-bottom:2px solid #eb19ff;color:#eb19ff}.tabs-vertical li.active a,.tabs-vertical li.active a:focus,.tabs-vertical li.active a:hover{background:#eb19ff;border-right:2px solid #eb19ff}.nav-pills>li.active>a,.nav-pills>li.active>a:focus,.nav-pills>li.active>a:hover{background:#eb19ff;color:#fff}.navbar-header,.right-sidebar .rpanel-title{background:#eb19ff;background:-moz-linear-gradient(to right,red 0,#ff9a00 10%,#d0de21 20%,#4fdc4a 30%,#3fdad8 40%,#2fc9e2 50%,#1c7fee 60%,#5f15f2 70%,#ba0cf8 80%,#fb07d9 90%,red 100%) 0 0/200% 100%;background:-webkit-linear-gradient(to right,red 0,#ff9a00 10%,#d0de21 20%,#4fdc4a 30%,#3fdad8 40%,#2fc9e2 50%,#1c7fee 60%,#5f15f2 70%,#ba0cf8 80%,#fb07d9 90%,red 100%) 0 0/200% 100%;background:linear-gradient(to right,red 0,#ff9a00 10%,#d0de21 20%,#4fdc4a 30%,#3fdad8 40%,#2fc9e2 50%,#1c7fee 60%,#5f15f2 70%,#ba0cf8 80%,#fb07d9 90%,red 100%) 0 0/200% 100%;animation:1s linear infinite a}@keyframes a{to{background-position:-200% 0}}
}
