css:
@-moz-document domain("lichess.org") {
/* Black Pieces */
.is2d .black.pawn {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/bp.png");
}

.is2d .black.knight {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/bn.png");
}

.is2d .black.bishop {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/bb.png");
}

.is2d .black.rook {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/br.png");
}

.is2d .black.queen {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/bq.png");
}

.is2d .black.king {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/bk.png");
}


/* White Pieces */
.is2d .white.pawn {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/wp.png");
}

.is2d .white.knight {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/wn.png");
}

.is2d .white.bishop {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/wb.png");
}

.is2d .white.rook {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/wr.png");
}

.is2d .white.queen {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/wq.png");
}

.is2d .white.king {
  background-image: url("https://www.chess.com/chess-themes/pieces/light/150/wk.png");
}


/* Background (Make Sure to Select Purple Board) */ 
.purple .is2d cg-board, #top div.color_demo.purple {
  background-image: url("https://www.chess.com/boards/light/150.png");
}

}
