css:
@-moz-document url-prefix("https://www.bing.com/") {
/* Dark mode for new Bing */
:root {
  bg-color: #121212;
  text-color: #ffffff;
  link-color: #9cf;
  accent-color: #f90;
}

body {
  background-color: var(--bg-color);
  color: var(--text-color);
}

a {
  color: var(--link-color);
}

.bing-logo,
.bing-searchbar,
.bing-chatbot {
  border-color: var(--accent-color);
}

.bing-chatbot .chat-message {
  background-color: var(--bg-color);
}}
